import React, { useEffect, useState, useContext} from "react";
import CircularProgress from "@material-ui/core/CircularProgress";
import InputParamsContext from './InputParamsContext';
import { buildParams } from '../utilities/utils';
import DisplayMap from "./DisplayMap";
import DisplayJson from "./DisplayJson";
import MapLegend from "./MapLegend";

const GetResults = () => {
	const [mapBlob, setMapBlob] = useState();
	const [jsonresp, setJsonresp] = useState();
	const [imgInfo, setImgInfo] = useState();
	const [resultsError, setResultsError] = useState();
	const [loading, setLoading] = useState(false);
	const [submittedParams, setSubmittedParams] = useState();
	const inputContext = useContext(InputParamsContext);

	const fetchResults = (jsonparams, output) => {
		fetch("https://grid2.rcc-acis.org/GridData", {body: jsonparams, method: "POST", headers: {'Content-Type': 'application/json'},})
			.then(response => {
				if (!response.ok) {
					setResultsError("Error fetching the results");
					return;
				}
				return output === 'png' ? response.blob() : response.json();
			})
			.then(response => {
				if (output === 'png') {
					setMapBlob(URL.createObjectURL(response));
				} else {
					setJsonresp(response.data);
				}
			})
			.catch(err => {
				if (err.message.includes("Failed to execute 'createObjectURL' on 'URL'")) {
					setResultsError("Invalid grid request - "+jsonparams);
				} else {
					setResultsError(err.message);
				};
			})
			.finally(() => {
				setLoading(false);
			});
	};

	const fetchInfo = (params) => {
		const infoInput = JSON.stringify({...params, output: "json"});
		fetch("https://grid2.rcc-acis.org/GridData", {body: infoInput, method: "POST", headers: {'Content-Type': 'application/json'},})
			.then(response => {
				if (!response.ok) {
					setResultsError("Error fetching the results info");
					return;
				}
				return response.json();
			})
			.then(response => {
				setImgInfo(response);
			})
			.catch(err => {
				if (err.message.includes("Failed to execute 'createObjectURL' on 'URL'")) {
					setResultsError("Invalid grid request - "+infoInput);
				} else {
					setResultsError(err.message);
				};
			});
	};

	useEffect(() => {
		setLoading(true);
		// Build parameters
		const input = buildParams(inputContext.inputParams);
		const jsonparams = JSON.stringify(input, null, 2);
		setSubmittedParams(jsonparams);
		// Get results
		fetchResults(jsonparams, input.output);
		if (input.output === 'png') {
			fetchInfo(input);
		}
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [inputContext.inputParams]);

	return (
		<>
			{loading &&
				<CircularProgress />
			}
			{submittedParams && (jsonresp || mapBlob) &&
				<>
					{mapBlob &&
						<DisplayMap 
							imgsrc={mapBlob}
							submittedParams={submittedParams}
							imgInfo={imgInfo}
						/>
					}
					{jsonresp &&
						<DisplayJson
							jsonresp={jsonresp}
							submittedParams={submittedParams}
						/>
					}
				</>
			}
			{resultsError &&
				<p>Error: {resultsError}</p>
			}
		</>
	);
}

export default GetResults;
