import React, { useEffect, useState, useContext } from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import CircularProgress from "@material-ui/core/CircularProgress";
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';

const useStyles = makeStyles((theme) => ({

}));

const CountySelect = (props) => {
	const { postals } = props;
    const [anchorEl, setAnchorEl] = useState(null);
    const [selectedIndex, setSelectedIndex] = useState(0);
    const [counties, setCounties] = useState();
    const [fetchError, setFetchError] = useState();
	const [loading, setLoading] = useState(false);
	const inputContext = useContext(InputParamsContext);
    const classes = useStyles();

    const fetchGeneral = (postals) => {
        const jsonparams = JSON.stringify({state: postals, meta: "name,id"});
        fetch("https://data.rcc-acis.org/General/county", {body: jsonparams, method: "POST", headers: {'Content-Type': 'application/json'}})
            .then(response => {
                if (!response.ok) {
                    setFetchError("Error fetching the list");
                    return;
                }
                return response.json()
            })
            .then(response => {
                setCounties(response.meta);
                return response.meta;
            })
            .catch(err => {
                if (err.message.includes("Failed to execute 'createObjectURL' on 'URL'")) {
                    setFetchError("Invalid request");
                } else {
                    setFetchError(err.message);
                };
            })
            .finally(() => {
                setLoading(false);
            });
    };

    useEffect(() => {
		// Reset 
		setFetchError();
		setLoading(true);
        // Get list
        if (postals) {
            fetchGeneral(postals);
        };
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [ postals ]);

	const handleClickListItem = (event) => {
		setAnchorEl(event.currentTarget);
	};

	const handleMenuItemClick = (event, index) => {
		setSelectedIndex(index);
		setAnchorEl(null);
		inputContext.updateInputParams({county: counties[index]});
	};

	const handleClose = () => {
		setAnchorEl(null);
	};

	return (
        <>
        {counties.length &&
            <>
            <List aria-label="County selection">
                <ListItem
                    button
                    aria-haspopup="true"
                    aria-controls="county-menu"
                    aria-label="selected county"
                    onClick={handleClickListItem}
                >
                    <ListItemText primary="Click to select county" secondary={"Selected: "+counties[selectedIndex].name.replace("County","")} />
                </ListItem>
            </List>
            <Menu
                id="county-menu"
                anchorEl={anchorEl}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose} 
            >
                {counties.map((option, index) => (
                    <MenuItem
                        key={option.fips}
                        selected={index === selectedIndex}
                        onClick={(event) => handleMenuItemClick(event, index)}
                    >
                        {option.name.replace("County","")}
                    </MenuItem>
                ))}
            </Menu>
            </>
        }
        {loading &&
            <CircularProgress size="1em" />
        }
        {fetchError &&
            <p>Error: {fetchError}</p>
        }
        </>
	);
 };

export default CountySelect;

