import React, { useEffect, useRef, useState, useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import MapGL, { NavigationControl } from 'react-map-gl';
import mapboxgl from 'mapbox-gl';
import 'mapbox-gl/dist/mapbox-gl.css';
import InputParamsContext from './InputParamsContext';
import { fetchBbox } from '../utilities/utils';

const useStyles = makeStyles((theme) => ({
	nav: {
		position: "absolute",
		top: 0,
		left: 0,
		padding: "3px",
	},
}));

// eslint-disable-next-line import/no-webpack-loader-syntax
mapboxgl.workerClass = require('worker-loader!mapbox-gl/dist/mapbox-gl-csp-worker').default;

const MapboxMap = (props) => {
	const { imgsrc } = props;
	const [ bbox, setBbox ] = useState()
	const [viewport, setViewPort ] = useState({
		width: "100%",
		height: "50vh",
		latitude: 42.4,
		longitude: -74.8,
		zoom: 4
  	});
	const inputContext = useContext(InputParamsContext);
	const classes = useStyles();
	const mapRef = useRef();

	const mapinfo = {
		token: "pk.eyJ1Ijoia2xlMSIsImEiOiJjank0b2dmd2swMm02M2NvMGNnY3c4aWNnIn0.GfryAbDLVsqX9Ir24eWlGQ",
//		mapstyle: "mapbox://styles/mapbox/streets-v11",
  	}

	const mapStyle = {
		'version': 8,
		'sources': {
		  'mapbox': {
			'type': 'vector',
			'url': 'mapbox://mapbox.mapbox-streets-v8',
		  },
		  'overlay': {
			'type': 'image',
			'url': imgsrc,
			'coordinates': [
				[parseFloat(bbox[0]), parseFloat(bbox[3])],
				[parseFloat(bbox[2]), parseFloat(bbox[3])],
				[parseFloat(bbox[2]), parseFloat(bbox[1])],
				[parseFloat(bbox[0]), parseFloat(bbox[1])]
			],
		  }
		},
		'layers': [
		  {
			'id': 'water',
			'source': 'mapbox',
			'source-layer': 'water',
			'type': 'fill',
			'paint': { 'fill-color': '#2c2c2c' }
		  },
		  {
			'id': 'boundaries',
			'source': 'mapbox',
			'source-layer': 'admin',
			'type': 'line',
		  },
		  {
			'id': 'overlay',
			'source': 'overlay',
			'type': 'raster',
			'paint': { 'raster-opacity': 0.85 }
		  }
		]
	}; 

	const handleViewportChange = viewport => setViewPort({...viewport, transitionDuration: 500 });

	useEffect(() => {
		if (inputContext.inputParams.areaDef.bbox) {
			setBbox(inputContext.inputParams.areaDef.bbox);
		} else {

			const postals = inputContext.inputParams.areaDef.state;
			console.log(postals)
			const meta = fetchBbox(postals)
			console.log(meta)

		}
	}, [inputContext.inputParams.areaDef]);

	return (
		<>
			<MapGL
				ref={mapRef}
				{...viewport}
				mapboxApiAccessToken={mapinfo.token}
				mapStyle={mapStyle}
				onViewportChange={handleViewportChange}
			>
				<div className={classes.nav}>
					<NavigationControl showCompass={false} />
				</div>
			</MapGL>
		</>
	);
}

export default MapboxMap;
