import { useContext, useState } from "react";
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Stack from '@mui/material/Stack';
import MenuItem from '@mui/material/MenuItem';
import dayjs from 'dayjs';
import InputParamsContext from './InputParamsContext';

const monthChoices = [
  {value: '01', label: 'January'}, 
  {value: '02', label: 'February'}, 
  {value: '03', label: 'March'}, 
  {value: '04', label: 'April'}, 
  {value: '05', label: 'May'}, 
  {value: '06', label: 'June'},
  {value: '07', label: 'July'}, 
  {value: '08', label: 'August'}, 
  {value: '09', label: 'September'}, 
  {value: '10', label: 'October'}, 
  {value: '11', label: 'November'}, 
  {value: '12', label: 'December'}
]

export default function DateFilter(props) {
  const inputContext = useContext(InputParamsContext);
  const [ smonth, setSmonth ] = useState('01');
  const [ emonth, setEmonth ] = useState('12');
  const [ sday, setSday ] = useState('15');
  const [ eday, setEday ] = useState('15');

  const selectMonthOptions = (choices) => {
    return (
      choices.map(option => {
        return (
          <MenuItem 
            dense={true}
            sx={{ pt:0, pb:0 }}
            key={option.value} 
            value={option.value}
          >
            {option.label}
          </MenuItem>
        )
      })
    )
  }

  const selectDayOptions = (month) => {
    const dim = dayjs('2024-' + month.toString() + '-01').daysInMonth()
    const dayChoices = Array.from({length: dim}, (v, k) => k+1)
    return (
      dayChoices.map(option => {
        return (
          <MenuItem 
            dense={true}
            sx={{ pt:0, pb:0 }}
            key={option} 
            value={option}
          >
            {option}
          </MenuItem>
        )
      })
    )
  }

  const handleChange = (event, valtyp='string') => {
		const name = event.target.name;
		const value = valtyp === 'integer' ? parseInt(event.target.value, 10) : event.target.value;
    switch(name) {
      case 'smonth':
        setSmonth(value);
        break;
      case 'emonth':
        setEmonth(value);
        break;
      case 'sday':
        setSday(value);
        break;
      case 'eday':
        setEday(value);
        break;
    }
    console.log(name, value, typeof value);
	}

  return (
      <Stack direction="row" spacing={0}>
        <TextField sx={{ m:0 }}
            select
            name="smonth"
            value={smonth}
            onChange={(e) => handleChange(e,'integer')}
        >
            {selectMonthOptions(monthChoices)}
        </TextField>
        <TextField sx={{ m:0 }}
            select
            name="sday"
            value={sday}
            onChange={(e) => handleChange(e,'integer')}
        >
            {selectDayOptions("smonth")}
        </TextField>

        <Typography variant="body2" sx={{ m: 1 }}>
            to
        </Typography>
        <TextField sx={{ m:0 }}
            select
            name="emonth"
            value={emonth}
            onChange={(e) => handleChange(e,'integer')}
        >
            {selectMonthOptions(monthChoices)}
        </TextField>
        <TextField sx={{ m:0 }}
            select
            name="eday"
            value={eday}
            onChange={(e) => handleChange(e,'integer')}
        >
            {selectDayOptions("emonth")}
        </TextField>
      </Stack>
  )
}
