import React, { useEffect, useRef, useState, useContext } from 'react';
import { Map, GeoJSON, TileLayer, CircleMarker, Popup, ZoomControl } from 'react-leaflet'
import Control from 'react-leaflet-control'
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';
import { multiStateBbox } from '../utilities/utils';

const useStyles = makeStyles((theme) => ({
	nav: {
		position: "absolute",
		top: 0,
		left: 0,
		padding: "3px",
	},
	mapglContainer: {
		border: "1pt solid midnightblue",
	},
}));

const MapboxMap = (props) => {
	const { imgsrc, submittedParams } = props;
	const [ bbox, setBbox ] = useState();
	const [ imgcoords, setImgcoords ] = useState();
	const [ resultsError, setResultsError ] = useState();
	const [viewport, setViewPort ] = useState({
		width: "100%",
		height: window.innerHeight * 0.6,
		zoom: 0,
		transitionDuration: 500
  	});
	const inputContext = useContext(InputParamsContext);
	const classes = useStyles();
	const mapRef = useRef();


	const mapStyle = (imgsrc,imgcoords) => {
		return ({
			version: 8,
			sources: {
				mapbox: {
					type: 'vector',
					url: 'mapbox://mapbox.mapbox-streets-v8',
				},
				overlay: {
					type: 'image',
					url: imgsrc,
					coordinates: imgcoords,
				}
			},
			layers: [
				{
					id: 'water',
					source: 'mapbox',
					'source-layer': 'water',
					type: 'fill',
					paint: { 'fill-color': '#2c2c2c' }
				},
				{
					id: 'boundaries',
					source: 'mapbox',
					'source-layer': 'admin',
					type: 'line',
				},
				{
					id: 'imgLayer',
					source: 'overlay',
					type: 'raster',
					paint: { 'raster-opacity': 0.90 }

				}
			]
		})
	}; 

	const fetchBbox = postals => {
		const jsonparams = JSON.stringify({state: postals, meta: "bbox"});
		fetch("https://data.rcc-acis.org/General/state", {body: jsonparams, method: "POST", headers: {'Content-Type': 'application/json'}})
			.then(response => {
				if (!response.ok) {
					setResultsError("Error");
				}
				return response.json();
			})
			.then(response => {
				if (response.meta && response.meta.length) {
					const bbox = multiStateBbox(response.meta);
					setBbox(bbox);
				} else {
					setResultsError("No bbox for "+postals);
				}
			})
			.catch(err => {
				if (err.message.includes("Failed to execute 'createObjectURL' on 'URL'")) {
					setResultsError("Invalid request");
				} else {
					setResultsError(err.message);
				};
			})
	};

	const handleViewportChange = viewport => setViewPort({...viewport });

	useEffect(() => {
		if (inputContext.inputParams.areaDef.bbox) {
			const bbox = JSON.parse(submittedParams).bbox;
			console.log(bbox)
			setBbox([parseFloat(bbox[0]),parseFloat(bbox[1]),parseFloat(bbox[2]),parseFloat(bbox[3])]);
		} else {
			const postals = inputContext.inputParams.areaDef.state;
			fetchBbox(postals);
		}
		// eslint-disable-next-line 
	}, [inputContext.inputParams.areaDef]);

	useEffect(() => {
		if (bbox) {
			// convert bbox to mapbox coordinates
			setImgcoords([ [bbox[0],bbox[3]], [bbox[2],bbox[3]], [bbox[2],bbox[1]], [bbox[0],bbox[1]] ]);
			// fit image in mapbox box
			const cid = document.getElementById('mapContainer');
			const cwidth = cid.parentElement.clientWidth;
			const cheight = window.innerHeight * 0.6;
			// Use WebMercatorViewport to get center longitude/latitude and zoom
			const wmviewport = new WebMercatorViewport({ width: cwidth, height: cheight})
				.fitBounds([[bbox[0],bbox[1]], [bbox[2],bbox[3]]], {padding:20})
			const { longitude, latitude, zoom } = wmviewport;
			console.log(bbox[0] + " " + bbox[1] + " " + bbox[2] + " " + bbox[3])
			setViewPort({...viewport, longitude, latitude, zoom, height:cheight});
		}
		// eslint-disable-next-line 
	}, [bbox]);


	return (
		<>
			<div className={classes.mapglContainer}>
				{viewport.zoom > 0 &&
					<MapGL
						ref={mapRef}
						{...viewport}
						mapboxApiAccessToken={token}
						mapStyle={mapStyle(imgsrc, imgcoords)}
						onViewportChange={handleViewportChange}
					>
						<div className={classes.nav}>
							<NavigationControl showCompass={false} />
						</div>
					</MapGL>
				}
			</div>
			{resultsError &&
				<p>Error: {resultsError}</p>
			}
		</>
	);
}

export default MapboxMap;
