import React, { useState, useEffect, useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import FormHelperText from '@material-ui/core/FormHelperText';
import InfoAdornment from './InfoAdornment';
import InputParamsContext from './InputParamsContext';
import { infoText } from '../utilities/constants';

const useStyles = makeStyles((theme) => ({
    colorsContainer: {
        marginTop: "0.75em",
    },
    colorFormControl: {
        width: "100%",
    },
	colorInputLabel: {
		color: "rgba(0,0,0,0.87)",
	},
}));

const ColorsSelect = () => {
	const [ colors, setColors ] = useState("");
	const [ colorError, setColorError ] = useState(false);
	const classes = useStyles();
	const inputContext = useContext(InputParamsContext);

	const handleColorChange = (event) => {
		const newcolors = event.target.value.replace(" ","");;
		setColors(newcolors);
		inputContext.updateInputParams({image: {...inputContext.inputParams.image, cmap: newcolors}});
	};

	useEffect(() => {
		if (inputContext.colors.server) {
			const newcolors = inputContext.colors.server.join(",");
			setColors(newcolors);	
		}
	}, [inputContext.colors.server]);

	useEffect(() => {
        console.log(inputContext.inputParams.image.levels)
		if (inputContext.inputParams.image.levels && colors) {
			const levelLength = inputContext.inputParams.image.levels.split(",").length;
            const colorLength = colors.split(",").length;
            setColorError(levelLength !== colorLength - 1);
		}
	}, [inputContext.inputParams.image.levels, colors]);

	return ( 
        <div className={classes.colorsContainer}>
            <FormControl variant="outlined" className={classes.colorFormControl} error={colorError}>
                <InputLabel htmlFor="colorInput" className={classes.colorInputLabel}>Colors</InputLabel>
                <OutlinedInput
                    value={colors}
                    id="colorInput"
                    label="Colors"
                    margin="dense"
                    inputProps={{ spellCheck: false }}
                    onChange={handleColorChange}
                    endAdornment={<InfoAdornment content={infoText.colors} />}
                />
                <FormHelperText>{colorError ? "Must have one more color than levels" : "Comma-separated color code list"}</FormHelperText>
            </FormControl>
		</div>
	);
}

export default ColorsSelect;
