import React, { useEffect, useRef } from "react";

const DisplayMap = (props) => {
	const {imgsrc} = props;
	const imgRef = useRef();

	useEffect(() => {
		imgRef.current.focus();
	})

	return (
		<div ref={imgRef} tabIndex="0" className="mapContainer">
			<img src={imgsrc} className="mapImage" alt="requested map" />
		</div>
	);
}

export default DisplayMap;
