import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Divider from '@material-ui/core/Divider';
import Drawer from '@material-ui/core/Drawer';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import Button from '@material-ui/core/Button';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ElementSelect from './ElementSelect';
import DateSelect from './DateSelect';
import AreaSelect from './AreaSelect';
import { drawerWidth } from '../utilities/constants';

const useStyles = makeStyles((theme) => ({
	menuButton: {
		"&:focus": {
			color: "#3f51b5",
			backgroundColor: "lightblue",
		},
		"&:hover": {
			color: "#3f51b5",
			backgroundColor: "lightblue",
		},
	},
	submitButton: {
		backgroundColor: "midnightblue",
		color: "white",
		"&:focus": {
			color: "midnightblue",
			backgroundColor: "lightblue",
		},
		"&:hover": {
			color: "midnightblue",
			backgroundColor: "lightblue",
		},
	},
	drawer: {
		width: drawerWidth,
		flexShrink: 0,
	},
	drawerPaper: {
		width: drawerWidth,
	},
	drawerHeader: {
		display: 'flex',
		alignItems: 'center',
		padding: theme.spacing(0, 1),
		// necessary for content to be below app bar
		...theme.mixins.toolbar,
		justifyContent: 'flex-end',
	},
}));

const OptionsSelection = (props) => {
	const {open, handleDrawerClose, handleViewMapClick} = props;
	const classes = useStyles();

	return (
		<>
			<Drawer
				className={classes.drawer}
				variant="persistent"
				anchor="left"
				open={open}
				classes={{ paper: classes.drawerPaper }}
			>
				<div className={classes.drawerHeader}>
					<Tooltip title="Close drawer">
						<IconButton className={classes.menuButton} aria-label="close drawer" onClick={handleDrawerClose}>
							<ChevronLeftIcon />
						</IconButton>
					</Tooltip>
				</div>
				<Divider />

				<ElementSelect />
				<DateSelect />
				<AreaSelect />

				<Tooltip title="view map">
					<Button variant="outlined" size="medium" className={classes.submitButton} onClick={handleViewMapClick}>
						View Map
					</Button>
				</Tooltip>
			</Drawer>
		</>
	)
}

export default OptionsSelection;
