import React, { useEffect } from "react";

const MapLegend = (props) => {
    const { imgInfo } = props;
    const { levels, cmap, data_bbox } = imgInfo;

    let labels = [];
    let from;
    let to;

    for (let i = 0; i < levels.length; i++) {
        from = levels[i];
        to = levels[i + 1];

        labels.push(
          '<i style="background:' +
            cmap[i] +
            '"></i> ' +
            from +
            (to ? "&ndash;" + to : "+")
        );
      }

      const ckey = labels.join("<br>");


    return (
        <>
            <p>{ckey}</p>
        </>     
    );
}

export default MapLegend;
