import React, {useState} from "react";

const ViewGeotiff = () => {

	const [mapBlob, setMapBlob] = useState();

	const fetchMap = () => {
		const input_params = JSON.stringify({
			state:"ne,ok",
			grid:"1",
			date:"2012-6",
			elems:[{
				name:"maxt",
				interval:"mly",
				duration:"mly",
				reduce:"max",
			}],
			output:"image",
			image:{
				proj:"lcc",
				overlays:["county:1:gray","state:2:purple"],
				interp:"cspline",
				width:350,
				levels:[90,95,100,105,110],
			},
		});
		fetch("http://data.rcc-acis.org/GridData?params="+input_params)
			.then(response => {
				if (!response.ok) {
					throw Error("Error fetching the map");
				}
				return response.blob()
			})
			.then(responseBlob => {
				setMapBlob(URL.createObjectURL(responseBlob));
			})
			.catch(err => {
				throw Error(err.message);
			});
	}

	return (
		<>
			<button onClick={fetchMap}>Get Map</button>
			{mapBlob &&
				<div>
					<img src={mapBlob} alt="requested map" />
				</div>
			}

		</>
	);
}

export default ViewGeotiff;
