import React, { useEffect } from "react";

const MapLegend = (props) => {
    const { imgInfo } = props;
    const { levels, cmap, data_bbox } = imgInfo;

    let labels = [];
    let from;
    let to;

        labels.push('<i style="background:' +
        cmap[0] +
        '"></i> ' +
        levels[0] +
        "-");

    for (let i = 0; i < levels.length; i++) {
        from = levels[i];
        to = levels[i + 1];


        labels.push(
          '<i style="background:' +
            cmap[i+1] +
            '"></i> ' +
            from +
            (to ? "&ndash;" + to : "+")
        );
      }

      const ckey = labels.join("<br>");


    return (
        <>
            {ckey}
        </>     
    );
}

export default MapLegend;
