// elements in Element selection menu
export const elements = [
    {menuName: 'Maximum temperature', name: 'maxt', reduce: 'mean'},	//set as default in Main
    {menuName: 'Minimum temperature', name: 'mint', reduce: 'mean'},
    {menuName: 'Average temperature', name: 'avgt', reduce: 'mean'},
    {menuName: 'Precipitation',       name: 'pcpn', reduce: 'sum'},
];

// text in State information popup
export const infoText = {
    postal: 'A list of state abbreviations will return an area emcompassing all states in the list. For example, "ME,WV" will return the Northeast (i.e. Maine to West Virginia).',
};
