import React, { useState, useContext } from 'react';
import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';

const monthChoices = ['January','February','March','April','May','June','July','August','September','October','November','December'];
const dayMonths30 = ['04','06','09','11'];

const useStyles = makeStyles((theme) => ({
	filterLabel: {
		display: "inline-block",
		lineHeight: "4.4em",
		verticalAlign: "bottom",
		marginRight: "0.25em",
		marginLeft: "0.25em",
	},
	filterGroup: {
		display: "inline-block",
		border: "1px solid rgba(0, 0, 0, 0.23)",
		borderRadius: "4px",
		padding: "0 0.6em",
		margin: "0.6em 0 0.6em 0",
	},
	filterGroupLegend: {
		fontSize: "95%",
	},
	textfield: {
		marginRight: "0.5em",
	},
	menuitem: {
		paddingTop: "1px",
		paddingBottom: "1px",
	},
}));

const RenderDateSelect = (props) => {
    const { dateLegend, dateParam } = props;
    const inputContext = useContext(InputParamsContext);
	const [, initMonth, initDay] = inputContext.inputParams.dateParam.split("-");
	const [filterMonth, setFilterMonth] = useState(initMonth);
	const [filterDay, setFilterDay] = useState(initDay);
    const classes = useStyles();

    console.log(initMonth + " " + initDay)

	const updateDate = (newMonth, newDay) => {
		const newdate = ["2020", newMonth, newDay].join("-");
		inputContext.updateInputParams(dateParam, newdate);	
	};

	const handleChangeMonth = () => event => {
		const newMonth = event.target.value;
        setFilterMonth(newMonth);
        if (filterDay === '31' && dayMonths30.includes(newMonth)) {
            setFilterDay('30');
            updateDate(newMonth, '30');
        } else if (filterDay >= '30' && event.target.value === '02') {
            setFilterDay('29');
            updateDate(newMonth, '29');
        } else {
            updateDate(newMonth, filterDay);
        };
	};

	const handleChangeDay = () => event => {
		const newDay = event.target.value;
		setFilterDay(newDay);
		updateDate(filterMonth, newDay);
	};

	const serialMonthOptions = () => {
        return (
            monthChoices.map((option,i) => {
                return (
                    <MenuItem 
                        key={"m"+i} 
                        className={classes.menuitem}
                        value={("0" + (i+1)).slice(-2)}
                    >
                        {option}
                </MenuItem>
                )
            })
        );
	};	

	const serialDayOptions = () => {
		let arrlen = 31;
		if (dayMonths30.includes(filterMonth)) {
			arrlen = 30;
		} else if (filterMonth === '02') {
			arrlen = 29;
		}
        return (
            Array.from({length: arrlen}, (v, k) => k+1).map((option,i) => {
                return (
                    <MenuItem 
                        key={"d"+i} 
                        className={classes.menuitem}
                        value={("0" + (i+1)).slice(-2)}
                    >
                        {option}
                </MenuItem>
                )
            })
        );
    };

	return (
		<div>
			<fieldset className={classes.filterGroup}>
				<legend className={classes.filterGroupLegend}>{dateLegend}</legend>
				<TextField
					select
					value={filterMonth}
					onChange={handleChangeMonth}
					className={classes.textfield}
					margin="dense"
					SelectProps={{ native: false }}
				>
					{serialMonthOptions}
				</TextField>
				<TextField
					select
					value={filterDay}
					onChange={handleChangeDay}
					margin="dense"
					SelectProps={{ native: false }}
				>
				    {serialDayOptions}
				</TextField>
			</fieldset>
		</div>
	)
}

export default RenderDateSelect;
