import React, { useEffect, useRef, useContext } from "react";
import InputParamsContext from './InputParamsContext';

const DisplayMap = (props) => {
	const {imgsrc} = props;
	const inputContext = useContext(InputParamsContext);
	const imgRef = useRef();

	useEffect(() => {
		imgRef.current.focus();
	});

	return (
		<>
			<img src={imgsrc} className="mapImage" ref={imgRef} tabIndex="0" alt="requested map" />
			<p>{JSON.stringify(inputContext.inputParams)}</p>
		</>
	);
}

export default DisplayMap;
