import React, { useEffect } from "react";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const MapLegend = (props) => {
    const { imgInfo } = props;
    const { levels, cmap, data_bbox } = imgInfo;

    let labels = [];
    levels.forEach((i) => {
        labels.push(
          '<i style="background:' +
            cmap[i+1] +
            '"></i> ' +
            levels[i] +
            (levels[i + 1] ? "&ndash;" + levels[i + 1] : "+")
        );
      });

      const ckey = labels.join("<br>");


    return (




          <TableContainer component={Paper}>
            <Table size="small" aria-label="color table">
              <TableBody>
                {levels.map((level, i) => (
                  <TableRow key={i}>
                    <TableCell align="right">{cmap[i+1]}</TableCell>
                    <TableCell align="right">{level}</TableCell>
                    <TableCell align="right">{levels[i+1]}</TableCell> 
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>







    );
}

export default MapLegend;
