import { useContext, useEffect, useState } from "react";
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Stack from '@mui/material/Stack';
import MenuItem from '@mui/material/MenuItem';
import dayjs from 'dayjs';
import InputParamsContext from './InputParamsContext';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles((theme) => ({
	dateSeperator: {
		display: "inline-block",
		lineHeight: "2em",
		verticalAlign: "bottom",
		margin: "0.5em 0.5em 0.25em",
	},
  menuitem: {
		paddingTop: "1px",
		paddingBottom: "1px",
    minHeight: "auto",
	},
}));

const monthChoices = [
  {value: '01', label: 'Jan'}, 
  {value: '02', label: 'Feb'}, 
  {value: '03', label: 'Mar'}, 
  {value: '04', label: 'Apr'}, 
  {value: '05', label: 'May'}, 
  {value: '06', label: 'Jun'},
  {value: '07', label: 'Jul'}, 
  {value: '08', label: 'Aug'}, 
  {value: '09', label: 'Sep'}, 
  {value: '10', label: 'Oct'}, 
  {value: '11', label: 'Nov'}, 
  {value: '12', label: 'Dec'}
]

export default function DateFilter(props) {
  const inputContext = useContext(InputParamsContext);
  const classes = useStyles();
  const [ smonth, setSmonth ] = useState('01');
  const [ emonth, setEmonth ] = useState('12');
  const [ sday, setSday ] = useState('01');
  const [ eday, setEday ] = useState('31');

const days_in_month = (month) => {
  // number of days in month (non-leap year)
  return dayjs('2023-' + month + '-01').daysInMonth()
}

  const selectMonthOptions = (choices) => {
    return (
      choices.map(option => {
        return (
          <MenuItem 
            dense={true}
            className={classes.menuitem}
            sx={{ pt:0, pb:0 }}
            key={option.value} 
            value={option.value}
          >
            {option.label}
          </MenuItem>
        )
      })
    )
  }

  const selectDayOptions = (month) => {
    const dim = days_in_month(month)
    const dayChoices = Array.from({length: dim}, (v, k) => (k+1).toString().padStart(2, '0'))
    return (
      dayChoices.map(option => {
        return (
          <MenuItem 
            dense={true}
            className={classes.menuitem}
            sx={{ pt:0, pb:0 }}
            key={option} 
            value={option}
          >
            {option}
          </MenuItem>
        )
      })
    )
  }

  const handleChange = (event) => {
		const name = event.target.name;
		const value = event.target.value;
    switch(name) {
      case 'smonth':
        const dim_new_month = days_in_month(value)
        console.log('days in new month' + dim_new_month)
        if (parseInt(sday) > parseInt(dim_new_month)) {
          setSday(dim_new_month)
        }
        setSmonth(value);
        break;
      case 'emonth':
        setEmonth(value);
        break;
      case 'sday':
        setSday(value);
        break;
      case 'eday':
        setEday(value);
        break;
    }
	}

  useEffect(() => {
    console.log(sday, eday)
  }, [smonth, emonth])

  return (
      <Stack direction="row" spacing={0}>
        <TextField size="small" sx={{ m:0 }}
            select
            name="smonth"
            value={smonth}
            onChange={handleChange}
        >
            {selectMonthOptions(monthChoices)}
        </TextField>
        <TextField size="small" sx={{ m:0 }}
            select
            name="sday"
            value={sday}
            onChange={handleChange}
        >
            {selectDayOptions(smonth)}
        </TextField>

        <Typography className={classes.dateSeperator}>
						-
				</Typography>

        <TextField size="small" sx={{ m:0 }}
            select
            name="emonth"
            value={emonth}
            onChange={handleChange}
        >
            {selectMonthOptions(monthChoices)}
        </TextField>
        <TextField size="small" sx={{ m:0 }}
            select
            name="eday"
            value={eday}
            onChange={handleChange}
        >
            {selectDayOptions(emonth)}
        </TextField>
      </Stack>
  )
}
