import React, { useEffect, useState, useContext} from "react";
import CircularProgress from "@material-ui/core/CircularProgress";
import DisplayMap from "./DisplayMap";
import InputParamsContext from './InputParamsContext';
import { buildParams } from '../utilities/utils';
import MapTitle from './MapTitle';

const GetImage = () => {
	const [mapBlob, setMapBlob] = useState();
	const [mapError, setMapError] = useState();
	const [loading, setLoading] = useState(false);
	const [submittedParams, setSubmittedParams] = useState();
	const inputContext = useContext(InputParamsContext);

	const fetchMap = (jsonparams) => {
		fetch("https://grid2.rcc-acis.org/GridData", {body: jsonparams, method: "POST"})
			.then(response => {
				if (!response.ok) {
					setMapError("Error fetching the map");
					return;
				}
				return response.blob()
			})
			.then(responseBlob => {
					setMapBlob(URL.createObjectURL(responseBlob));
				})
			.catch(err => {
				setMapError(err.message);
			})
			.finally(() => {
				setLoading(false);
			});
	};

	useEffect(() => {
		// Reset 
		setMapBlob();
		setMapError();
		setLoading(true);
		// Build parameters
		const jsonparams = buildParams(inputContext.inputParams);
		setSubmittedParams(jsonparams);
		// Get map
		fetchMap(jsonparams);
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [inputContext.inputParams]);

	return (
		<>
			{loading &&
				<CircularProgress />
			}
			{mapBlob && submittedParams &&
				<>
					<MapTitle />
					<DisplayMap 
						imgsrc={mapBlob}
					/>
					<p>{submittedParams}</p>
				</>
			}
			{mapError &&
				<p>Error: {mapError}</p>
			}
		</>
	);
}

export default GetImage;
