import React, { useState, useContext } from 'react';
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';

const useStyles = makeStyles((theme) => ({
	heading: {
		marginTop: 0,
	},
	imageOptionsContainer: {
		borderLeft: "1pt solid midnightblue",
		borderRight: "1pt solid midnightblue",
		borderBottom: "1pt solid midnightblue",
		padding: "0",
	},
	accordionSummaryContainer: {
		padding: "0.25em 1em",
	},
}));

const ImageSelect = () => {
	const classes = useStyles();
	const inputContext = useContext(InputParamsContext);


	return ( 
		<div className={classes.imageOptionsContainer}>
			<Accordion>
				<AccordionSummary
					expandIcon={<ExpandMoreIcon />}
					aria-controls="image-options-content"
					id="image-options-header"
					className={classes.accordionSummaryContainer}
				>
					<Typography className={classes.heading} variant="body1">More Options</Typography>
				</AccordionSummary>
				<AccordionDetails>
					<Typography>
						Lorem ipsum dolor sit amet, consectetur adipiscing elit. Suspendisse malesuada lacus ex,
						sit amet blandit leo lobortis eget.
					</Typography>
				</AccordionDetails>
			</Accordion>
		</div>
	);
}

export default ImageSelect;
