import React, { useEffect, useState, useContext} from "react";
import CircularProgress from "@material-ui/core/CircularProgress";
import DisplayMap from "./DisplayMap";
import InputParamsContext from './InputParamsContext';

const GetImage = () => {
	const [mapBlob, setMapBlob] = useState();
	const [mapError, setMapError] = useState();
	const [loading, setLoading] = useState(false);
	const inputContext = useContext(InputParamsContext);

	const dateDiff = (date1, date2) => {
		const sdate = new Date(date1);
		const edate = new Date(date2);
		const one_day = 1000 * 60 * 60 * 24;
		return (Math.round((edate.getTime() - sdate.getTime()) / one_day));
	};
	const fetchMap = () => {
		// Reset 
		setMapBlob();
		setMapError();
		setLoading(true);

		// Calculate duration based on sdate and edate
		const input_params = inputContext.inputParams;
		const duration = dateDiff(input_params.sdate, input_params.edate) + 1
		const newElems = input_params.elems;
		newElems[0] = {...newElems[0], duration}
		const paramsToSubmit = {...input_params, date:input_params.edate, elems: newElems};
		delete paramsToSubmit.sdate;
		delete paramsToSubmit.edate;

		fetch("https://grid2.rcc-acis.org/GridData", {body: JSON.stringify(paramsToSubmit), method: "POST"})
			.then(response => {
				if (!response.ok) {
					setMapError("Error fetching the map");
					return;
				}
				return response.blob()
			})
			.then(responseBlob => {
					setMapBlob(URL.createObjectURL(responseBlob));
				})
			.catch(err => {
				setMapError(err.message);
			})
			.finally(() => {
				setLoading(false);
			});
	}

	useEffect(() => {
		fetchMap();
	}, [inputContext.inputParams]);

	return (
		<>
			{loading &&
				<CircularProgress />
			}
			{mapBlob &&
				<DisplayMap 
					imgsrc={mapBlob}
				/>
			}
			{mapError &&
				<p>Error: {mapError}</p>
			}
		</>
	);
}

export default GetImage;
