import React, { useState, useContext } from 'react';
import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';

const useStyles = makeStyles((theme) => ({
	dateGroup: {
		display: "inline-block",
		border: "1px solid rgba(0, 0, 0, 0.23)",
		borderRadius: "4px",
		padding: "0 0.6em",
		margin: "0.6em 0 0.6em 0",
		fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
	},
	dateGroupLegend: {
		fontSize: "95%",
	},
	textfield: {
		marginRight: "0.5em",
	},
	menuitem: {
		paddingTop: "1px",
		paddingBottom: "1px",
	},
}));

const RenderDate = (props) => {
	const { dateLegend, dateParam, justMonth=false } = props;
	const inputContext = useContext(InputParamsContext);
	const [, initMonth, initDay] = inputContext.inputParams[dateParam].split("-");
	const [selectMonth, setSelectMonth] = useState(initMonth);
	const [selectDay, setSelectDay] = useState(initDay);
	const classes = useStyles();
	const monthChoices = ['January','February','March','April','May','June','July','August','September','October','November','December'];
	const dayMonths30 = ['04','06','09','11'];

	const updateDate = (newMonth, newDay) => {
		const newdate = ["2020", newMonth, newDay].join("-");
		inputContext.updateInputParams({[dateParam]:newdate});	
	};

	const handleChangeMonth = () => event => {
		const newMonth = event.target.value;
		setSelectMonth(newMonth);
		if (selectDay === '31' && dayMonths30.includes(newMonth)) {
			setSelectDay('30');
			updateDate(newMonth, '30');
		} else if (selectDay >= '30' && event.target.value === '02') {
			setSelectDay('29');
			updateDate(newMonth, '29');
		} else {
			updateDate(newMonth, selectDay);
		};
	};

	const handleChangeDay = () => event => {
		const newDay = event.target.value;
		setSelectDay(newDay);
		updateDate(selectMonth, newDay);
	};

	const serialMonthOptions = () => {
		return (
			monthChoices.map((option,i) => {
				return (
					<MenuItem 
						key={"m"+i} 
						className={classes.menuitem}
						value={("0" + (i+1)).slice(-2)}
					>
						{option}
				</MenuItem>
				)
			})
		);
	};	

	const serialDayOptions = () => {
		let arrlen = 31;
		if (dayMonths30.includes(selectMonth)) {
			arrlen = 30;
		} else if (selectMonth === '02') {
			arrlen = 29;
		}
		return (
			Array.from({length: arrlen}, (v, k) => k+1).map((option,i) => {
				return (
					<MenuItem 
						key={"d"+i} 
						className={classes.menuitem}
						value={("0" + (i+1)).slice(-2)}
					>
						{option}
				</MenuItem>
				)
			})
		);
	};

	return (
		<>
			<fieldset className={classes.dateGroup}>
				<legend className={classes.dateGroupLegend}>{dateLegend}</legend>
				<TextField
					select
					value={selectMonth}
					id="selectMonth"
					onChange={handleChangeMonth()}
					className={classes.textfield}
					margin="dense"
					SelectProps={{ native: false }}
				>
					{serialMonthOptions()}
				</TextField>
				{!justMonth &&
					<TextField
						select
						value={selectDay}
						id="selectDay"
						onChange={handleChangeDay()}
						margin="dense"
						SelectProps={{ native: false }}
					>
						{serialDayOptions()}
					</TextField>
				}
			</fieldset>
		</>
	)
}

export default RenderDate;
