import React, { useState, useContext } from 'react';
import TextField from '@material-ui/core/TextField';
import Typography from '@material-ui/core/Typography';
import MenuItem from '@material-ui/core/MenuItem';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';

const monthChoices = ['January','February','March','April','May','June','July','August','September','October','November','December'];
const dayMonths30 = ['04','06','09','11'];

const useStyles = makeStyles((theme) => ({
	dateRangeContainer: {
		border: "1pt solid midnightblue",
		padding: "0.5em 1em",
	},
	filterLabel: {
		display: "inline-block",
		lineHeight: "4.4em",
		verticalAlign: "bottom",
		marginRight: "0.25em",
		marginLeft: "0.25em",
	},
	filterGroup: {
		display: "inline-block",
		border: "1px solid rgba(0, 0, 0, 0.23)",
		borderRadius: "4px",
		padding: "0 0.6em",
		margin: "0.6em 0 0.6em 0",
	},
	filterGroupLegend: {
		fontSize: "95%",
	},
	textfield: {
    marginRight: "0.5em",
	},
	menuitem: {
		paddingTop: "1px",
		paddingBottom: "1px",
	}
}));

const DateRange = () => {
	const [fromFilterMonth, setFromFilterMonth] = useState('01');
	const [fromFilterDay, setFromFilterDay] = useState('01');
	const [toFilterMonth, setToFilterMonth] = useState('12');
	const [toFilterDay, setToFilterDay] = useState('31');
	const inputContext = useContext(InputParamsContext);
	const classes = useStyles();

	const handleChangeMonth = (name) => event => {
		if (name === 'fromFilterMonth') {
			setFromFilterMonth(event.target.value);
			if (fromFilterDay === '31' && dayMonths30.includes(event.target.value)) {
				setFromFilterDay('30');
				updateDates('sdate', event.target.value, '30');
			} else if (fromFilterDay >= '30' && event.target.value === '02') {
				setFromFilterDay('29');
				updateDates('sdate', event.target.value, '29');
			} else {
				updateDates('sdate', event.target.value, fromFilterDay);
			};
		} else if (name === 'toFilterMonth') {
			setToFilterMonth(event.target.value)
			if (toFilterDay === '31' && dayMonths30.includes(event.target.value)) {
				setToFilterDay('30');
				updateDates('edate', event.target.value, '30');
			} else if (toFilterDay >= '30' && event.target.value === '02') {
				setToFilterDay('29');
				updateDates('edate', event.target.value, '29');
			} else {
				updateDates('edate', event.target.value, toFilterDay);
			}
		}
		updateDates();
	};

	const handleChangeDay = (name) => event => {
		if (name === 'fromFilterDay') {
			setFromFilterDay(event.target.value);
		} else if (name === 'toFilterDay') {
			setToFilterDay(event.target.value);
		}
		updateDates();
	};

	const updateDates = (se, newMonth, newDay) => {
		console.log(inputContext.inputParams.sdate)
		console.log(inputContext.inputParams.edate)
		const newdate = ["2020", newMonth, newDay].join("-");
		console.log('new' + " " + se + " " + newdate)
		inputContext.updateInputParams(se, newdate);	
	};

	const serialStringMonthOptions = (choices) => {
    return (
      choices.map((option,i) => {
        return (
					<MenuItem 
						key={i} 
						className={classes.menuitem}
						value={("0" + (i+1)).slice(-2)}
					>
            {option}
          </MenuItem>
        )
      })
    );
	};	

	const serialStringDayOptions = (fromto) => {
		let arrlen = 31;
		if ((fromto === 'from' && dayMonths30.includes(fromFilterMonth)) || (fromto === 'to' && dayMonths30.includes(toFilterMonth)) ) {
			arrlen = 30;
		} else if ((fromto === 'from' && fromFilterMonth === '02') || (fromto === 'to' && toFilterMonth === '02')) {
			arrlen = 29;
		} else {
			arrlen = 31;
		}
    return (
      Array.from({length: arrlen}, (v, k) => k+1).map((option,i) => {
        return (
					<MenuItem 
						key={fromto+i} 
						className={classes.menuitem}
						value={("0" + (i+1)).slice(-2)}
					>
            {option}
          </MenuItem>
        )
      })
    );
  };

	return (
		<div className={classes.dateRangeContainer}>
			<fieldset className={classes.filterGroup}>
				<legend className={classes.filterGroupLegend}>Start date</legend>
				<TextField
					select
					id="fromFilterMonth"
					value={fromFilterMonth}
					onChange={handleChangeMonth('fromFilterMonth')}
					className={classes.textfield}
					margin="dense"
					SelectProps={{ native: false }}
				>
					{serialStringMonthOptions(monthChoices)}
				</TextField>
				<TextField
					select
					id="fromFilterDay"
					value={fromFilterDay}
					onChange={handleChangeDay('fromFilterDay')}
					margin="dense"
					SelectProps={{ native: false }}
					>
						{serialStringDayOptions('from')}
				</TextField>
			</fieldset>
			<Typography className={classes.filterLabel}>
				 -
			</Typography>
			<fieldset className={classes.filterGroup}>
				<legend className={classes.filterGroupLegend}>End date</legend>
				<TextField
					select
					id="toFilterMonth"
					value={toFilterMonth}
					onChange={handleChangeMonth('toFilterMonth')}
					className={classes.textfield}
					margin="dense"
					SelectProps={{ native: false }}
					>
						{serialStringMonthOptions(monthChoices)}
				</TextField>
				<TextField
					select
					id="toFilterDay"
					value={toFilterDay}
					onChange={handleChangeDay('toFilterDay')}
					margin="dense"
					SelectProps={{ native: false }}
					>
						{serialStringDayOptions('to')}
				</TextField>
			</fieldset>
		</div>
	)
}

export default DateRange;
