import React, { useState, useEffect, useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import FormHelperText from '@material-ui/core/FormHelperText';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableRow from '@material-ui/core/TableRow';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import InfoAdornment from './InfoAdornment';
import InputParamsContext from './InputParamsContext';
import { infoText } from '../utilities/constants';

const useStyles = makeStyles((theme) => ({
    colorsContainer: {
        marginTop: "0.75em",
    },
    colorFormControl: {
        width: "100%",
    },
	colorInputLabel: {
		color: "rgba(0,0,0,0.87)",
	},
    colorbarContainer: {
        marginTop: "0.75em",
    },
    listContainer: {
		marginTop: "0.5em",
		border: "1pt solid lightgray",
		padding: 0,
	},
}));

const ColorsSelect = () => {
    const [anchorEl, setAnchorEl] = useState(null);
	const [selectedIndex, setSelectedIndex] = useState(0);
	const [ colors, setColors ] = useState([]);
	const [ colorError, setColorError ] = useState(false);
	const classes = useStyles();
	const inputContext = useContext(InputParamsContext);

    const colormaps = require('../utilities/colorbrewer.json');
    const colormap_keys = Object.keys(colormaps);

    const handleClickListItem = (event) => {
		setAnchorEl(event.currentTarget);
	};

    const handleMenuItemClick = (event, index, cmp) => {
		setSelectedIndex(index);
		setAnchorEl(null);
        const levelcnt = inputContext.inputParams.image.levels.length >= 0 ? inputContext.inputParams.image.levels.split(",").length : inputContext.levels.server.length;
		const newColors = colormaps[cmp][levelcnt + 1];
        setColors(newColors);
        console.log(newColors);
        if (inputContext.levels.client.length === 0) {
            inputContext.updateInputParams({image: {...inputContext.inputParams.image, cmap: newColors, levels: inputContext.levels.server.join(",")}});
        } else {
    		inputContext.updateInputParams({image: {...inputContext.inputParams.image, cmap: newColors}});
        }

	};

	const handleClose = () => {
		setAnchorEl(null);
	};

	const handleColorChange = (event) => {
		//const newcolors = event.target.value.replace(" ","");;
		//setColors(newcolors);
        //if (inputContext.levels.client.length === 0) {
        //    inputContext.updateInputParams({image: {...inputContext.inputParams.image, cmap: newcolors, levels: inputContext.levels.server.join(",")}});
        //} else {
    	//	inputContext.updateInputParams({image: {...inputContext.inputParams.image, cmap: newcolors}});
        //}
	};

	useEffect(() => {
		if (inputContext.colors.server) {
			setColors(inputContext.colors.server);	
		}
	}, [inputContext.colors.server]);

	useEffect(() => {
		if (typeof inputContext.inputParams.image.levels === 'string' && inputContext.inputParams.image.levels.length >= 0 && colors) {
			const levelLength = inputContext.inputParams.image.levels.split(",").length;
            const colorLength = colors.length;
            setColorError(levelLength !== colorLength - 1);
            console.log('colorerror ' + (levelLength !== colorLength - 1));
		}
	}, [inputContext.inputParams.image.levels, colors]);

	return ( 
        <div className={classes.colorsContainer}>
            <List className={classes.listContainer} aria-label="color selection">
				<ListItem
					button
					aria-haspopup="true"
					aria-controls="color-map-menu"
					aria-label="selected-color"
					onClick={handleClickListItem}
				>
					<ListItemText primary="Click to change color map" secondary={"Currently selected: "+colormap_keys[selectedIndex]} />
				</ListItem>
			</List>
			<Menu
				id="color-map-menu"
				anchorEl={anchorEl}
				keepMounted
				open={Boolean(anchorEl)}
				onClose={handleClose} 
			>
				{colormap_keys.map((cmp, index) => (
					<MenuItem
						key={cmp}
						selected={index === selectedIndex}
                        value={cmp}
						onClick={(event) => handleMenuItemClick(event, index, cmp)}
					>
						{cmp}
					</MenuItem>
				))}
			</Menu>

			<TableContainer className={classes.colorbarContainer}>
				{colors &&
					<Table size="small" padding="none" aria-label="color table">
						<TableBody>
							<TableRow>
								{colors.map((cs, i) => (
									<TableCell key={i} style={{backgroundColor:"" + cs + ""}}>&nbsp;</TableCell>
								))}
							</TableRow>	
						</TableBody>
					</Table>
				}
			</TableContainer>
		</div>
	);
}

export default ColorsSelect;
