import React, {useState} from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Drawer from '@material-ui/core/Drawer';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import Tab from '@material-ui/core/Tab';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import TabContext from '@material-ui/lab/TabContext';
import TabList from '@material-ui/lab/TabList';
import TabPanel from '@material-ui/lab/TabPanel';
import Home from './Home'
import PcpnRegion from './PcpnRegion'
import PcpnStation from './PcpnStation'
import UsgsRegion from './UsgsRegion'
import UsgsStation from './UsgsStation'
import ResvRegion from './ResvRegion'
import ResvStation from './ResvStation'

const drawerWidth = 200;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  appBar: {
    maxWidth: document.body.clientWidth,  // Added to prevent appbar from extending beyond body width - KLE
    right: 'auto',                        // ditto
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    width: `calc(${document.body.clientWidth}px - ${drawerWidth}px)`, // Also made this clientWidth
    marginLeft: drawerWidth,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: theme.spacing(2),
    "&:focus": {
      color: "#3f51b5",
      backgroundColor: "lightblue",
    },
    "&:hover": {
      color: "#3f51b5",
      backgroundColor: "lightblue",
    },
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(0),  // changed from 3
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: 0, // example had this as -drawerWidth
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: drawerWidth,  // example had this as 0
  },
  tabPanel: {
    padding: "1rem",
  },
  styledTab: {
    textTransform: 'capitalize',
    fontSize: '1.0rem',
    minHeight: '2.25rem',
    padding: '0',
    "&:focus": {
      backgroundColor: "lightblue",
    },
    "&:hover": {
      backgroundColor: "lightblue",
    },
  },
}));

const Main = () => {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [value, setValue] = useState('home');

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const handleValueChange = (event, value) => {
    const newValue = value ? value : event.currentTarget.value;  //tab clicks are value; button clicks are event.currentTarget.value
    setTimeout(() => {setValue(newValue)}, 200); //need timeout to have time for drawer to close before rendering results
  };

  return (
    <>
      <AppBar 
        role="banner"
        position="fixed"
        className={clsx(classes.appBar, { [classes.appBarShift]: open, })}>
        <a className="skip" href="#maincontent">Skip navigation</a>
        <Toolbar>
          <Tooltip title="Open menu">
            <IconButton
              color="inherit"
              aria-label="open menu drawer"
              onClick={handleDrawerOpen}
              edge="start"
              className={clsx(classes.menuButton, open && classes.hide)}
            >
              <MenuIcon />
            </IconButton>
          </Tooltip>
          <Typography tabIndex="0" variant="h6" component="h1">
            Massachusetts Drought Dashboard
          </Typography>
        </Toolbar>
      </AppBar>

      <TabContext value={value}>
        <nav>
          <Drawer
            className={classes.drawer}
            variant="persistent"
            anchor="left"
            open={open}
            classes={{ paper: classes.drawerPaper }}
          >
            <div className={classes.drawerHeader}>
              <Tooltip title="Close menu">
                <IconButton className={classes.menuButton} aria-label="close menu" onClick={handleDrawerClose}>
                  <ChevronLeftIcon />
                </IconButton>
              </Tooltip>
            </div>
            <Divider />
            <TabList onChange={handleValueChange} orientation="vertical" aria-label="tab navigation">
              <Tab className={classes.styledTab} tabIndex="0" onClick={handleDrawerClose} label="Home" value="home" />
              <Tab className={classes.styledTab} tabIndex="0" onClick={handleDrawerClose} label="Precipitation" value="pcpnregion" />
              <Tab className={classes.styledTab} tabIndex="0" onClick={handleDrawerClose} label="Streamflow" value="sfregion" />
              <Tab className={classes.styledTab} tabIndex="0" onClick={handleDrawerClose} label="Groundwater" value="gwregion" />
              <Tab className={classes.styledTab} tabIndex="0" onClick={handleDrawerClose} label="Lakes/Impoundments" value="liregion" />

              <Tab className={classes.hide} label="Precipitation Station Table" value="pcpnstation" />
              <Tab className={classes.hide} label="Streamflow Site Table" value="sfstation" />
              <Tab className={classes.hide} label="Groundwater Site Table" value="gwstation" />
              <Tab className={classes.hide} label="Lakes/Impoundments Table" value="listation" />
            </TabList>
          </Drawer>
        </nav>

        <main 
          id="maincontent" 
          tabIndex="-1"
          className={clsx(classes.content, { [classes.contentShift]: open, })}>
          <div className={classes.drawerHeader} />
          <TabPanel className={classes.tabPanel} value="home">
            <Home></Home>
          </TabPanel>
          <TabPanel className={classes.tabPanel} value="pcpnregion">
            <PcpnRegion
              handleButtonClick={handleValueChange}
            />
          </TabPanel>
          <TabPanel className={classes.tabPanel} value="sfregion">
            <UsgsRegion
              handleButtonClick={handleValueChange}
              sfgw="sf"
            />
          </TabPanel>
          <TabPanel className={classes.tabPanel} value="gwregion">
            <UsgsRegion
              handleButtonClick={handleValueChange}
              sfgw="gw"
            />
          </TabPanel>
          <TabPanel className={classes.tabPanel} value="liregion">
            <ResvRegion
              handleButtonClick={handleValueChange}
            />
          </TabPanel>
          <TabPanel className={classes.tabPanel} value="pcpnstation">
            <PcpnStation></PcpnStation>
          </TabPanel>
          <TabPanel className={classes.tabPanel} value="sfstation">
            <UsgsStation sfgw="sf"></UsgsStation>
          </TabPanel>
          <TabPanel className={classes.tabPanel} value="gwstation">
            <UsgsStation sfgw="gw"></UsgsStation>
          </TabPanel>
          <TabPanel className={classes.tabPanel} value="listation">
            <ResvStation></ResvStation>
          </TabPanel>
        </main>
      </TabContext>
    </>
  )
}

export default Main;
