import React, {useState} from "react";
import CircularProgress from "@material-ui/core/CircularProgress";
import DisplayMap from "./DisplayMap";

const ViewGeotiff = () => {

	const [mapBlob, setMapBlob] = useState();
	const [mapError, setMapError] = useState();
	const [loading, setLoading] = useState(false);

	const fetchMap = () => {
		const input_params = {
			"grid":"nrccc-nn",
			"state":"ME,WV",
			"sdate":"2020-01",
			"edate":"2020-01",
			"elems":[{
				"name":"pcpn",
				"interval":[0,1],
				"duration":1,
				"reduce":"sum"
			}],
			"output":"gif"
		};

		// Reset 
		setMapBlob();
		setMapError();
		setLoading(true);

		fetch("https://grid2.rcc-acis.org/GridData", {body: JSON.stringify(input_params), method: "POST"})
			.then(response => {
				if (!response.ok) {
					setMapError("Error fetching the map");
					return;
				}
				return response.blob()
			})
			.then(responseBlob => {
					setMapBlob(URL.createObjectURL(responseBlob));
				})
			.catch(err => {
				setMapError(err.message);
			})
			.finally(() => {
				setLoading(false);
			});
	}

	return (
		<>
			<button onClick={fetchMap}>Get Map</button>
			<div>
				{loading &&
					<CircularProgress />
				}
				{mapBlob &&
					<DisplayMap 
						isrc={mapBlob}
					/>
				}
				{mapError &&
					<p>Error: {mapError}</p>
				}
			</div>
		</>
	);
}

export default ViewGeotiff;


