import React, {useState} from 'react';
import clsx from 'clsx';
import Banner from './Banner';
import OptionsSelection from './OptionsSelection';
import GetImage from './GetImage';
import { makeStyles } from '@material-ui/core/styles';

const drawerWidth = 400;

const useStyles = makeStyles((theme) => ({
     drawerHeader: {
      display: 'flex',
      alignItems: 'center',
      padding: theme.spacing(0, 1),
      // necessary for content to be below app bar
      ...theme.mixins.toolbar,
      justifyContent: 'flex-end',
    },
    content: {
      flexGrow: 1,
      padding: theme.spacing(0),  // changed from 3
      transition: theme.transitions.create('margin', {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
      marginLeft: 0, // example had this as -drawerWidth
    },
    contentShift: {
      transition: theme.transitions.create('margin', {
        easing: theme.transitions.easing.easeOut,
        duration: theme.transitions.duration.enteringScreen,
      }),
      marginLeft: drawerWidth,  // example had this as 0
    },
  }));

  const Main = () => {
  const classes = useStyles();
  const [open, setOpen] = useState(true);
  const [viewMap, setViewMap] = useState(false);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const handleViewMapClick = () => {
    setViewMap(true);
  }

  return (
    <>
      <Banner
        open={open}
        handleDrawerOpen={handleDrawerOpen}
      />

      <nav>
        <OptionsSelection
          open={open}
          handleDrawerClose={handleDrawerClose}
          handleViewMapClick={handleViewMapClick}
        />
      </nav>

      <main>
        <div className={clsx(classes.content, { [classes.contentShift]: open, })}>
          <div className={classes.drawerHeader}  />
          {viewMap &&
            <GetImage />
          }
          {!viewMap &&
            <>
              <p>... Introductory text here ... Set options and click "View Map" button.</p>
            </>
          }
        </div>
      </main>
    </>
  )
}

export default Main;
