import React, {useContext} from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';

const options = [
	{name: 'Maximum temperature', abbr: 'maxt'},
	{name: 'Minimum temperature', abbr: 'mint'},
	{name: 'Average temperature', abbr: 'avgt'},
	{name: 'Precipitation', abbr: 'pcpn'},
	{name: 'Snowfall', abbr: 'snow'},
];

const useStyles = makeStyles((theme) => ({
	listContainer: {
		border: "1pt solid midnightblue",
	},
}));

const ElementMenu = () => {
	const [anchorEl, setAnchorEl] = React.useState(null);
	const [selectedIndex, setSelectedIndex] = React.useState(0);
	const inputContext = useContext(InputParamsContext);
	const classes = useStyles();

	const handleClickListItem = (event) => {
		setAnchorEl(event.currentTarget);
	};

	const handleMenuItemClick = (event, index) => {
		setSelectedIndex(index);
		setAnchorEl(null);
		const elemName = options[index].abbr;
		const newElems = {...inputContext.inputParams.elems[0], name: elemName};
		inputContext.updateInputParams('elems', newElems);
	};

	const handleClose = () => {
		setAnchorEl(null);
	};

	return (
		<div>
			<List className={classes.listContainer} aria-label="Element selection">
				<ListItem
					button
					aria-haspopup="true"
					aria-controls="element-menu"
					aria-label="selected element"
					onClick={handleClickListItem}
				>
					<ListItemText primary="Click to select element" secondary={options[selectedIndex]} />
				</ListItem>
			</List>
			<Menu
				id="element-menu"
				anchorEl={anchorEl}
				keepMounted
				open={Boolean(anchorEl)}
				onClose={handleClose} 
			>
				{options.map((option, index) => (
					<MenuItem
						key={option}
						selected={index === selectedIndex}
						onClick={(event) => handleMenuItemClick(event, index)}
					>
						{option.name}
					</MenuItem>
				))}
			</Menu>
		</div>
	);
}

export default ElementMenu;
