import React, { useState, useContext } from 'react';
import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';
import { months } from '../utilities/constants';

// This component was modified from the original to only allow month selection

const useStyles = makeStyles((theme) => ({
	dateGroup: {
		display: "inline-block",
		border: "1px solid rgba(0, 0, 0, 0.23)",
		borderRadius: "4px",
		padding: "0 0.6em",
		margin: "0.6em 0 0.6em 0",
		fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
	},
	dateGroupLegend: {
		fontSize: "95%",
	},
	textfield: {
		marginRight: "0.5em",
	},
	menuitem: {
		paddingTop: "1px",
		paddingBottom: "1px",
	},
}));

const RenderDate = (props) => {
	const { dateLegend, dateParam } = props;
	const inputContext = useContext(InputParamsContext);
	const [, initMonth] = inputContext.inputParams[dateParam === 'date' ? 'edate' : dateParam].split("-");
	const [selectMonth, setSelectMonth] = useState(initMonth);
	const classes = useStyles();

	const updateDate = (newMonth) => {
		const newdate = ["2019", newMonth].join("-");
		if (dateParam === 'date') {
			inputContext.updateInputParams({sdate:newdate, edate:newdate});
		} else {
			inputContext.updateInputParams({[dateParam]:newdate});
		}
	};

	const handleChangeMonth = () => event => {
		const newMonth = event.target.value;
		setSelectMonth(newMonth);
		updateDate(newMonth);
	};

	const serialMonthOptions = () => {
		return (
			months.map((option,i) => {
				return (
					<MenuItem 
						key={"m"+i} 
						className={classes.menuitem}
						value={("0" + (i+1)).slice(-2)}
					>
						{option}
					</MenuItem>
				)
			})
		);
	};	

	return (
		<>
			<fieldset className={classes.dateGroup}>
				<legend className={classes.dateGroupLegend}>{dateLegend}</legend>
				<TextField
					select
					value={selectMonth}
					id="selectMonth"
					onChange={handleChangeMonth()}
					className={classes.textfield}
					margin="dense"
					SelectProps={{ native: false }}
				>
					{serialMonthOptions()}
				</TextField>
			</fieldset>
		</>
	)
}

export default RenderDate;
