import React, { useEffect, useRef, useState, useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import MapGL, { NavigationControl, Layer } from 'react-map-gl';
import mapboxgl from 'mapbox-gl';
import 'mapbox-gl/dist/mapbox-gl.css';
import InputParamsContext from './InputParamsContext';
import { multiStateBbox } from '../utilities/utils';

const useStyles = makeStyles((theme) => ({
	nav: {
		position: "absolute",
		top: 0,
		left: 0,
		padding: "3px",
	},
}));

// eslint-disable-next-line import/no-webpack-loader-syntax
mapboxgl.workerClass = require('worker-loader!mapbox-gl/dist/mapbox-gl-csp-worker').default;

const MapboxMap = (props) => {
	const { imgsrc } = props;
	const [ bbox, setBbox ] = useState([-75,40,-74,41]);
	const [ resultsError, setResultsError ] = useState();
	const [viewport, setViewPort ] = useState({
		width: "100%",
		height: "50vh",
		latitude: 42.4,
		longitude: -74.8,
		zoom: 4
  	});
	const inputContext = useContext(InputParamsContext);
	const classes = useStyles();
	const mapRef = useRef();

	const mapinfo = {
		token: "pk.eyJ1Ijoia2xlMSIsImEiOiJjank0b2dmd2swMm02M2NvMGNnY3c4aWNnIn0.GfryAbDLVsqX9Ir24eWlGQ",
//		mapstyle: "mapbox://styles/mapbox/streets-v11",
  	}

	const mapStyle = {
		version: 8,
		sources: {
		  mapbox: {
			type: 'vector',
			url: 'mapbox://mapbox.mapbox-streets-v8',
		  },
		  overlay: {
			type: 'image',
			url: imgsrc,
			coordinates: [
				[(bbox[0]), (bbox[3])],
				[(bbox[2]), (bbox[3])],
				[(bbox[2]), (bbox[1])],
				[(bbox[0]), (bbox[1])]
			],
		  }
		},
		layers: [
		  {
			id: 'water',
			source: 'mapbox',
			'source-layer': 'water',
			type: 'fill',
			paint: { 'fill-color': '#2c2c2c' }
		  },
		  {
			id: 'boundaries',
			source: 'mapbox',
			'source-layer': 'admin',
			type: 'line',
		  },
		]
	}; 

	const imgLayer = {
		id: 'imgLayer',
		source: 'overlay',
		type: 'raster',
		paint: { 'raster-opacity': 0.90 }
	};

	const fetchBbox = postals => {
		const jsonparams = JSON.stringify({state: postals, meta: "bbox"});
		fetch("https://data.rcc-acis.org/General/state", {body: jsonparams, method: "POST", headers: {'Content-Type': 'application/json'}})
			.then(response => {
				if (!response.ok) {
					setResultsError("Error");
				}
				return response.json();
			})
			.then(response => {
				if (response.meta && response.meta.length) {
					const bbox = multiStateBbox(response.meta);
					setBbox([parseFloat(bbox[0]),parseFloat(bbox[1]),parseFloat(bbox[2]),parseFloat(bbox[3])]);
				} else {
					setResultsError("No bbox for "+postals);
				}
			})
			.catch(err => {
				if (err.message.includes("Failed to execute 'createObjectURL' on 'URL'")) {
					setResultsError("Invalid request");
				} else {
					setResultsError(err.message);
				};
			})
	};

	const handleViewportChange = viewport => setViewPort({...viewport, transitionDuration: 500 });

	useEffect(() => {
		if (inputContext.inputParams.areaDef.bbox) {
			const bbox = inputContext.inputParams.areaDef.bbox;
			setBbox(bbox);
		} else {
			const postals = inputContext.inputParams.areaDef.state;
			fetchBbox(postals);
		}
	}, [inputContext.inputParams.areaDef]);


	return (
		<>
			<MapGL
				ref={mapRef}
				{...viewport}
				mapboxApiAccessToken={mapinfo.token}
				mapStyle={mapStyle}
				onViewportChange={handleViewportChange}
			>
				<div className={classes.nav}>
					<NavigationControl showCompass={false} />
				</div>
				{bbox &&
					<Layer {...imgLayer} />
				}
			</MapGL>
			{resultsError &&
				<p>Error: {resultsError}</p>
			}
		</>
	);
}

export default MapboxMap;
