import React, {useState} from 'react';
import clsx from 'clsx';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import Banner from './Banner';
import OptionsSelection from './OptionsSelection';
import GetResults from './GetResults';
import InputParamsContext from './InputParamsContext';
import { default_params, drawerWidth } from '../utilities/constants';

const useStyles = makeStyles((theme) => ({
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(2),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: 0,
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: drawerWidth,
  },
}));

const Main = () => {
  const classes = useStyles();
  const [viewMap, setViewMap] = useState(false);
  const [inputParams, setInputParams] = useState(default_params)
  const theme = useTheme();
	const smallScreen = useMediaQuery(theme.breakpoints.down('sm'))
  const [open, setOpen] = useState(!smallScreen);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const handleViewMapClick = () => {
    setViewMap(true);
    if (smallScreen) {
      setOpen(false);
    }
  };

  const updateInputParams = (updateParams) => {
    setViewMap(false);
    const newParams = {...inputParams, ...updateParams}
    setInputParams(newParams);
  }

  return (
    <InputParamsContext.Provider value={{ inputParams, updateInputParams }}>
      <>
        <a href="#maincontent" className="skip">Skip to main content</a>

        <Banner
          open={open}
          handleDrawerOpen={handleDrawerOpen}
        />

        <nav>
          <OptionsSelection
            open={open}
            handleDrawerClose={handleDrawerClose}
            handleViewMapClick={handleViewMapClick}
          />
        </nav>

        <main id="maincontent">
          <div className={classes.drawerHeader}  />
          <div className={clsx(classes.content, { [classes.contentShift]: open, })}>
            {viewMap &&
              <GetResults />
            }
            {!viewMap &&
              <>
                <Typography paragraph>
                ... Introductory text here, such as ... 
                </Typography> 
                <Typography paragraph>
                  Climate Normals are three-decade averages of climatological variables including temperature and precipitation. 
                  These normals are updated once every 10 years. The 1991-2020 U.S. Climate normals dataset is the latest release of 
                  NCEIâ€™s Climate Normals. This initial release of the gridded climate normals dataset contains monthly Normals of 
                  temperature (maximum, miminum, average) and precipitation
                </Typography>
                <Typography>
                  To view the data contained in the gridded climate normals dataset, set your options in the
                  navigation pane and click the "Submit Request" button to view the results. Buttons will be provided below each
                  resulting product to allow you to download the data in a variety of formats.
                </Typography>
                </>
            }
          </div>
        </main>

      </>
    </InputParamsContext.Provider>
  )
}

export default Main;
