import React from 'react';
import clsx from 'clsx';
import Typography from '@material-ui/core/Typography';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import MenuIcon from '@material-ui/icons/Menu';

import { makeStyles } from '@material-ui/core/styles';

const drawerWidth = 400;const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  appBar: {
     transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    backgroundColor:"midnightblue",
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`, // Also made this clientWidth
    marginLeft: drawerWidth,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    backgroundColor:"midnightblue",
  },
  menuButton: {
    marginRight: theme.spacing(2),
    "&:focus": {
      color: "#3f51b5",
      backgroundColor: "lightblue",
    },
    "&:hover": {
      color: "#3f51b5",
      backgroundColor: "lightblue",
    },
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(0),  // changed from 3
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: 0, // example had this as -drawerWidth
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: drawerWidth,  // example had this as 0
  },
  tabPanel: {
    padding: "1rem",
  },
  styledTab: {
    textTransform: 'capitalize',
    fontSize: '1.0rem',
    minHeight: '2.25rem',
    padding: '0',
    "&:focus": {
      backgroundColor: "lightblue",
    },
    "&:hover": {
      backgroundColor: "lightblue",
    },
  },
}));

const Banner = (props) => {
  const {open, handleDrawerOpen} = props;
  const classes = useStyles();

  return (
    <>
     <AppBar 
        role="banner"
        position="fixed"
        className={clsx(classes.appBar, { [classes.appBarShift]: open, })}>
        <Toolbar>
          <Tooltip title="Open setup">
            <IconButton
              color="inherit"
              aria-label="open options drawer"
              onClick={handleDrawerOpen}
              edge="start"
              className={clsx(classes.menuButton, open && classes.hide)}
            >
              <MenuIcon />
            </IconButton>
          </Tooltip>
          <Typography tabIndex="0" variant="h6" component="h1">
            Gridded NCEI Normals Viewer
          </Typography>
        </Toolbar>
      </AppBar>
    </>
  )
}

export default Banner;
