import React, {useState} from 'react';
import clsx from 'clsx';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import Drawer from '@material-ui/core/Drawer';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import useStyles from './useStyles';
import Banner from './Banner';
import OptionsSelection from './OptionsSelection';
import GetImage from './GetImage';

const Main = () => {
  const classes = useStyles();
  const [open, setOpen] = useState(true);
  const [getMap, setGetMap] = useState(false);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const handleGetMapClick = () => {
    setGetMap(true);
  }

  return (
    <>
      <Banner
        classes={classes}
        open={open}
        handleDrawerOpen={handleDrawerOpen}
      />

      <nav>
        <OptionsSelection
          classes={classes}
          open={open}
          handleDrawerClose={handleDrawerClose}
          handleGetMapClick={handleGetMapClick}
        />
      </nav>

      <main>
        <div className={clsx(classes.content, { [classes.contentShift]: open, })}>
          <div className={classes.drawerHeader}  />
          {getMap &&
            <GetImage />
          }
        </div>
      </main>
    </>
  )
}

export default Main;
