import React, {useContext} from 'react';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';
import { elements, months } from '../utilities/constants';

const useStyles = makeStyles((theme) => ({
	titleContainer: {
		margin: "0 0 1em",
	},
}));

const MapTitle = () => {
	const inputContext = useContext(InputParamsContext);
    const classes = useStyles();

    const selectedElem = inputContext.inputParams.elems[0].name;
    const elemInfo = elements.find((elements) => elements.name === selectedElem);
    const selectedDuration = inputContext.inputParams.elems[0].duration;
    const selectedInterval = inputContext.inputParams.elems[0].interval.length === 3 ? 'Daily' : 'Monthly';
    const selectedGrid =  inputContext.inputParams.grid;
    const [,smn,sdy] = inputContext.inputParams.sdate.split("-");
    const selectedStart = months[parseInt(smn)-1] + " " + parseInt(sdy);
    const selectedDate = selectedInterval === 'Monthly' ? inputContext.inputParams.edate.slice(5,7) : inputContext.inputParams.edate.slice(5);

	return (
		<div className={classes.titleContainer}>
            <Typography variant="h6">
                {selectedDuration === 1 && selectedInterval+" "}
                {elemInfo.menuName} 
                {!(selectedInterval === 'Daily' && selectedDuration === 1) && ' ('+elemInfo.reduce+")"}
            </Typography>
            {selectedDuration > 1 &&
                <Typography variant="subtitle1">
                   Interval: {selectedStart} through {selectedDate} ({selectedDuration} days)
                </Typography>
            }
            {selectedDuration === 1 &&
                <Typography variant="subtitle1">
                    Date: {selectedDate}
                </Typography>
            }
            <Typography variant="subtitle1" gutterBottom>
                Grid: {selectedGrid}
            </Typography>
		</div>
	);
}

export default MapTitle;
