import React, { useEffect, useState, useContext } from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import CircularProgress from "@material-ui/core/CircularProgress";
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';

const useStyles = makeStyles((theme) => ({

}));

const CountySelect = (props) => {
	const { postals } = props;
	const inputContext = useContext(InputParamsContext);
    const [anchorEl, setAnchorEl] = React.useState(null);
    const [selectedIndex, setSelectedIndex] = React.useState(0);
    const [fetchError, setFetchError] = useState();
	const [loading, setLoading] = useState(false);
    const classes = useStyles();

    const counties = [{menuName:"Broome"}, {menuName:"St. Lawrence"}]

    useEffect(() => {
		// Reset 
		setFetchError();
		setLoading(true);
		// Get list
		fetchCounties(postals);
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [inputContext.inputParams]);

	const handleClickListItem = (event) => {
		setAnchorEl(event.currentTarget);
	};

	const handleMenuItemClick = (event, index) => {
		setSelectedIndex(index);
		setAnchorEl(null);
		inputContext.updateInputParams({county: counties[index]});
	};

	const handleClose = () => {
		setAnchorEl(null);
	};

	return (
        <>
            <List aria-label="County selection">
                <ListItem
                    button
                    aria-haspopup="true"
                    aria-controls="county-menu"
                    aria-label="selected county"
                    onClick={handleClickListItem}
                >
                    <ListItemText primary="Click to select county" secondary={"Selected: "+counties[selectedIndex].menuName} />
                </ListItem>
            </List>
            <Menu
                id="county-menu"
                anchorEl={anchorEl}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose} 
            >
                {loading &&
                    <CircularProgress />
                }
                {fetchError &&
				    <p>Error: {fetchError}</p>
			    }
                {counties &&
                counties.map((option, index) => (
                    <MenuItem
                        key={option.menuName}
                        selected={index === selectedIndex}
                        onClick={(event) => handleMenuItemClick(event, index)}
                    >
                        {option.menuName}
                    </MenuItem>
                ))}
            </Menu>
        </>
	);
 };

export default CountySelect;

/*
