import React, { useEffect, useRef, useState } from 'react';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import MapGL, { NavigationControl } from 'react-map-gl';
import mapboxgl from 'mapbox-gl';
import DisplayRequest from './DisplayRequest';
import 'mapbox-gl/dist/mapbox-gl.css';

const useStyles = makeStyles((theme) => ({
	downloadButton: {
		margin: "1em 0.5em",
	},
	mapImage: {
		width: "100%",
	},
	nav: {
		position: "absolute",
		top: 0,
		left: 0,
		padding: "3px",
	},
}));

// eslint-disable-next-line import/no-webpack-loader-syntax
mapboxgl.workerClass = require('worker-loader!mapbox-gl/dist/mapbox-gl-csp-worker').default;

const DisplayMap = (props) => {
	const { imgsrc, submittedParams } = props;
	const [showParams, setShowParams] = useState(false);
	const [viewport, setViewPort ] = useState({
		width: "100%",
		height: "50vh",
		latitude: 42.4,
		longitude: -74.8,
		zoom: 4
  	});
	const classes = useStyles();
	const imgRef = useRef();
	const mapRef = useRef();

	const mapinfo = {
		token: "pk.eyJ1Ijoia2xlMSIsImEiOiJjank0b2dmd2swMm02M2NvMGNnY3c4aWNnIn0.GfryAbDLVsqX9Ir24eWlGQ",
		mapstyle: "mapbox://styles/mapbox/streets-v11",
  	}








	const mapStyle = {
		'version': 8,
		'sources': {
		  'mapbox': {
			'type': 'vector',
			'url': 'mapbox://mapbox.mapbox-streets-v8'
		  },
		  'overlay': {
			'type': 'image',
			'url': '%PUBLIC_URL%nemap.png',
			'coordinates': [
				[-79.95970329697062, 46.54645497007963],
				[-69.66501014096089, 46.54645497007963],
				[-69.66501014096083, 39.33905737461734],
				[-79.95970329697053, 39.3390573746173]
			  ],
		  }
		},
		'layers': [
		  {
			'id': 'water',
			'source': 'mapbox',
			'source-layer': 'water',
			'type': 'fill',
			'paint': { 'fill-color': '#2c2c2c' }
		  },
		  {
			'id': 'boundaries',
			'source': 'mapbox',
			'source-layer': 'admin',
			'type': 'line',
		  },
		  {
			'id': 'overlay',
			'source': 'overlay',
			'type': 'raster',
			'paint': { 'raster-opacity': 0.85 }
		  }
		]
	}; 

	const handleViewportChange = viewport => setViewPort({...viewport, transitionDuration: 500 });

	const handleShowParamsClick = () => {
		setShowParams(prev => !prev);
	}

	useEffect(() => {
		imgRef.current.focus();
	});

	return (
		<div>
			<img src={imgsrc} className={classes.mapImage} ref={imgRef} tabIndex="0" alt="requested map" />


			<MapGL
				ref={mapRef}
				{...viewport}
				mapboxApiAccessToken={mapinfo.token}
				mapStyle={mapStyle}
				onViewportChange={handleViewportChange}
			>
				<div className={classes.nav}>
					<NavigationControl showCompass={false} />
				</div>
			</MapGL>



			<Button variant="contained" size="small" className={classes.downloadButton}>Download image (png)</Button>
			<Button variant="contained" size="small" className={classes.downloadButton}>Download image (geotiff)</Button>
			<Button variant="contained" size="small" className={classes.downloadButton}>Download data (json)</Button>
			<Button variant="contained" size="small" className={classes.downloadButton} onClick={handleShowParamsClick}>Display request (json)</Button>
			<br />
			{showParams &&
				<DisplayRequest 
					submittedParams={submittedParams}
				/>
			}
		</div>
	);
}

export default DisplayMap;
