import React, {useContext} from 'react';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';

const useStyles = makeStyles((theme) => ({
	areaContainer: {
		borderLeft: "1pt solid midnightblue",
		borderRight: "1pt solid midnightblue",
		borderBottom: "1pt solid midnightblue",
		borderTop: "0",
		padding: "1em 1em",
	},}));

const AreaSelect = () => {
	const inputContext = useContext(InputParamsContext);
	const classes = useStyles();

	return (
		<div className={classes.areaContainer}>
			<Typography variant="body1">
				Select area
			</Typography>
			<p>Bounding box, region, state, drag rectangle, etc.</p>

			<TextField
				id="stateInput"
				label="State(s)"
				defaultValue="NY,PA"
				helperText="Enter one or more state postal abbreviations"
				variant="outlined"
				autofocus={true}
				margin="dense"
			/>

<FormControl variant="outlined">
        <InputLabel htmlFor="component-outlined">Name</InputLabel>
        <OutlinedInput id="component-outlined" value={name} onChange={handleChange} label="Name" />
      </FormControl>

		</div>
	);
}

export default AreaSelect;
