import React, { useEffect, useRef } from 'react';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import DisplayRequest from './DisplayRequest';

const useStyles = makeStyles((theme) => ({
	downloadButton: {
		margin: "1em 0.5em",
	},
}));

const DisplayMap = (props) => {
	const {imgsrc} = props;
	const imgRef = useRef();
	const classes = useStyles();

	useEffect(() => {
		imgRef.current.focus();
	});

	return (
		<div>
			<img src={imgsrc} className="mapImage" ref={imgRef} tabIndex="0" alt="requested map" />
			<Button variant="contained" className={classes.downloadButton}>Download image (png)</Button>
			<Button variant="contained" className={classes.downloadButton}>Download image (geotiff)</Button>
			<Button variant="contained" className={classes.downloadButton}>Download data (json)</Button>
			<br />
			<DisplayRequest 
				submittedParams={submittedParams}
			/>
		</div>
	);
}

export default DisplayMap;
