import React, { useState, useContext } from 'react';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import FormHelperText from '@material-ui/core/FormHelperText';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';
import InfoAdornment from './InfoAdornment';
import { infoText } from '../utilities/constants';

const useStyles = makeStyles((theme) => ({
	bboxInputLabel: {
		color: "rgba(0,0,0,0.87)",
		fontSize: "1.2em",
	},
}));

const BboxSelect = () => {
    const inputContext = useContext(InputParamsContext);
    const [bbox, setBbox] = useState(inputContext.inputParams.bbox || ['','','','']);
    const classes = useStyles();

    const handleChange = (event, index) => {
        const newBbox = bbox;
        newBbox[index] = event.target.value;
		setBbox(newBbox);
		inputContext.updateInputParams({bbox:newBbox});	
	};

    return (
        <FormControl variant="outlined">
            <InputLabel htmlFor="stateInput" className={classes.bboxInputLabel}>West</InputLabel>
            <OutlinedInput
                value={bbox[0]}
                id="bboxInputW"
                label="boundingBoxWest"
                onChange={(event) => handleChange(event, 0)}
                endAdornment={<InfoAdornment content={infoText.postal} />}
            />
            <FormHelperText id="bboxInputW">Enter West Longitudes as negative values</FormHelperText>
        </FormControl>
        );
    }

export default BboxSelect;
