const checkBbox = bbox => {
	if (bbox[0] < maxbbox.west) {
		bbox[0] = maxbbox.west;
	}
	if (bbox[1] < maxbbox.south) {
		bbox[1] = maxbbox.south;
	}
	if (bbox[2] > maxbbox.east) {
		bbox[2] = maxbbox.east;
	}
	if (bbox[3] > maxbbox.north) {
		bbox[3] = maxbbox.north;
	}
	return bbox;
};

// check point 
const checkLoc = loc => {
	loc[0] = parseFloat(loc[0]);
	loc[1] = parseFloat(loc[1]);
	if (loc[0] < maxbbox.west) {
		loc[0] = maxbbox.west;
	}
	if (loc[1] < maxbbox.south) {
		loc[1] = maxbbox.south;
	}
	if (loc[0] > maxbbox.east) {
		loc[0] = maxbbox.east;
	}
	if (loc[1] > maxbbox.north) {
		loc[1] = maxbbox.north;
	}
	return loc;
};


// find bounding box around one or more states
const multiStateBbox = meta => {
	var bbox = {xn:0, xs:999, xw:0, xe:-999};
	meta.forEach(element => {
		const [w, s, e, n] = [...element.bbox];
		if (n > bbox.xn) { bbox.xn = n; }
		if (s < bbox.xs) { bbox.xs = s; }
		if (w < bbox.xw) { bbox.xw = w; }
		if (e > bbox.xe) { bbox.xe = e; }
	});
	const newbbox = [bbox.xw,bbox.xs,bbox.xe,bbox.xn];
	return checkBbox(newbbox);
};

