import React, { useState, useEffect, useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableRow from '@material-ui/core/TableRow';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import InputParamsContext from './InputParamsContext';
import { infoText } from '../utilities/constants';

const useStyles = makeStyles((theme) => ({
    colorsContainer: {
        marginTop: "0.75em",
    },
    colorFormControl: {
        width: "100%",
    },
	colorInputLabel: {
		color: "rgba(0,0,0,0.87)",
	},
    colorbarContainer: {
        margin:"1.5em 6px 2em",
    },
    listContainer: {
		border: "1pt solid lightgray",
		padding: 0,
	},
    errormsg: {
        color: "red",
        marginTop: "6px",
        fontSize: "90%",
    },
}));

const ColorsSelect = () => {
    const [anchorEl, setAnchorEl] = useState(null);
	const [selectedIndex, setSelectedIndex] = useState(13);
	const [ colors, setColors ] = useState([]);
	const [ colorError, setColorError ] = useState(false);
	const classes = useStyles();
	const inputContext = useContext(InputParamsContext);

    const colormaps = require('../utilities/colorbrewer.json');
    const colormap_keys = Object.keys(colormaps);

    const handleClickListItem = (event) => {
		setAnchorEl(event.currentTarget);
	};

    const handleMenuItemClick = (event, index, cmp) => {
		setSelectedIndex(index);
		setAnchorEl(null);
        const levelcnt = (inputContext.inputParams.image.levels && inputContext.inputParams.image.levels.length >= 0) ? inputContext.inputParams.image.levels.split(",").length : inputContext.levels.server.length;
        if (colormaps[colormap_keys[selectedIndex]].hasOwnProperty(levelcnt + 1)) {
		    const newColors = colormaps[cmp][levelcnt + 1];
            setColors(newColors);
            if (inputContext.levels.client.length === 0) {
                inputContext.updateInputParams({image: {...inputContext.inputParams.image, cmap: newColors, levels: inputContext.levels.server.join(",")}});
            } else {
                inputContext.updateInputParams({image: {...inputContext.inputParams.image, cmap: newColors}});
            }
            setColorError(false);
        } else {
            setColors([]);
            inputContext.updateInputParams({image: {...inputContext.inputParams.image, cmap: []}});
            setColorError(true);
        }
	};

	const handleClose = () => {
		setAnchorEl(null);
	};

	useEffect(() => {
		if (inputContext.colors.server) {
			setColors(inputContext.colors.server);
            setSelectedIndex(13);
            setColorError(false);
		}
	}, [inputContext.colors.server]);

	useEffect(() => {
		if (typeof inputContext.inputParams.image.levels === 'string' && inputContext.inputParams.image.levels.length >= 0) {
			const levelLength = inputContext.inputParams.image.levels.split(",").length;
            if (colormaps[colormap_keys[selectedIndex]].hasOwnProperty(levelLength + 1)) {
                const newColors = colormaps[colormap_keys[selectedIndex]][levelLength + 1];
                setColors(newColors);
                inputContext.updateInputParams({image: {...inputContext.inputParams.image, cmap: newColors}});
                setColorError(false);
            } else {
                setColors([]);
                inputContext.updateInputParams({image: {...inputContext.inputParams.image, cmap: []}});
                setColorError(levelLength === 1 ? false : true);
            }
		}
        // eslint-disable-next-line react-hooks/exhaustive-deps
	}, [inputContext.inputParams.image.levels]);

	return ( 
        <div className={classes.colorsContainer}>
            {colors.length > 0 &&
                <Grid container spacing={0}>
					<Grid item>
                        <List className={classes.listContainer} aria-label="color selection">
                            <ListItem
                                button
                                aria-haspopup="true"
                                aria-controls="color-map-menu"
                                aria-label="selected-color"
                                onClick={handleClickListItem}
                            >
                                <ListItemText primary="Click to change color map" secondary={"Currently selected: "+colormap_keys[selectedIndex]} />
                            </ListItem>
                        </List>
                        <Menu
                            id="color-map-menu"
                            anchorEl={anchorEl}
                            keepMounted
                            open={Boolean(anchorEl)}
                            onClose={handleClose} 
                        >
                            {colormap_keys.map((cmp, index) => (
                                <MenuItem
                                    key={cmp}
                                    selected={index === selectedIndex}
                                    onClick={(event) => handleMenuItemClick(event, index, cmp)}
                                >
                                    <TableContainer>
                                        <Table padding="none" aria-label="color table">
                                            <TableBody>
                                                <TableRow>
                                                    {colormaps[cmp][colors.length].map((cs, i) => (
                                                        <TableCell key={i} style={{backgroundColor:"" + cs + ""}}>&nbsp;</TableCell>
                                                    ))}
                                                    <TableCell style={{paddingLeft: "1em"}}>{cmp}</TableCell>
                                                </TableRow>	
                                            </TableBody>
                                        </Table>
                                    </TableContainer>

                                </MenuItem>
                            ))}
                        </Menu>
					</Grid>

					<Grid item>
                        {!colorError &&
                            <TableContainer className={classes.colorbarContainer}>
                                    <Table size="small" padding="none" aria-label="color table">
                                        <TableBody>
                                            <TableRow>
                                                {colors.map((cs, i) => (
                                                    <TableCell key={i} style={{width:"1em", backgroundColor:"" + cs + ""}}>&nbsp;</TableCell>
                                                ))}
                                            </TableRow>	
                                        </TableBody>
                                    </Table>
                            </TableContainer>
                        }
					</Grid>
				</Grid>
            }

            {colorError &&
                <Typography variant="body1" className={classes.errormsg} gutterBottom>
                    {infoText.colors}
                </Typography>
            }

		</div>
	);
}

export default ColorsSelect;
