import React, { useEffect, useState, useContext} from "react";
import CircularProgress from "@material-ui/core/CircularProgress";
import DisplayMap from "./DisplayMap";
import InputParamsContext from './InputParamsContext';

const GetImage = () => {
	const [mapBlob, setMapBlob] = useState();
	const [mapError, setMapError] = useState();
	const [loading, setLoading] = useState(false);
	const inputContext = useContext(InputParamsContext);

	const fetchMap = () => {
		const input_params = inputContext.inputParams;
		// Reset 
		setMapBlob();
		setMapError();
		setLoading(true);

		fetch("https://grid2.rcc-acis.org/GridData", {body: JSON.stringify(input_params), method: "POST"})
			.then(response => {
				if (!response.ok) {
					setMapError("Error fetching the map");
					return;
				}
				return response.blob()
			})
			.then(responseBlob => {
					setMapBlob(URL.createObjectURL(responseBlob));
				})
			.catch(err => {
				setMapError(err.message);
			})
			.finally(() => {
				setLoading(false);
			});
	}

	useEffect(() => {
		fetchMap();
	}, [inputContext.inputParams]);

	return (
		<>
			{loading &&
				<CircularProgress />
			}
			{mapBlob &&
				<DisplayMap 
					imgsrc={mapBlob}
				/>
			}
			{mapError &&
				<p>Error: {mapError}</p>
			}
		</>
	);
}

export default GetImage;
