import React, { useState, useContext } from 'react';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormLabel from '@material-ui/core/FormLabel';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';


const useStyles = makeStyles((theme) => ({
    formControl: {
		marginTop: "0.5em",
		marginBottom: "1em",
    },
    radioButton: {
		color: "midnightblue !important",
        paddingRight: "3px",
        fontSize: "90%",
	},
}));

const SubstateSelect = (props) => {
    const { postals } = props;
    const inputContext = useContext(InputParamsContext);
    const [stateType, setStateType] = useState('all');
    const classes = useStyles();

    const handleTypeChange = (event) => {
        setStateType(event.target.value);
    };

    return (
        <>
            <FormControl component="fieldset" className={classes.formControl}>
                <FormLabel component="legend">State options:</FormLabel>
                <RadioGroup row aria-label="state type" name="statetype" value={stateType} onChange={handleTypeChange}>
                    <FormControlLabel value="all" control={<Radio size="small" className={classes.radioButton} />} label="Entire area" />
                    <FormControlLabel value="county" control={<Radio size="small" className={classes.radioButton} />} label="County" />
                    <FormControlLabel value="climdiv" control={<Radio disabled size="small" className={classes.radioButton} />} label="Climate division" />
                </RadioGroup>
            </FormControl>

            {stateType === 'county' &&
                "To be implemented"
            }
        </>
    );

 }

export default SubstateSelect;
