import React, { useEffect, useState, useContext } from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import CircularProgress from "@material-ui/core/CircularProgress";
import Typography from "@material-ui/core/Typography";
import InputParamsContext from './InputParamsContext';

const SubstateSelect = (props) => {
	const { postals, stateType } = props;
    const [anchorEl, setAnchorEl] = useState(null);
    const [selectedIndex, setSelectedIndex] = useState(0);
    const [counties, setCounties] = useState();
    const [fetchError, setFetchError] = useState();
	const [loading, setLoading] = useState(false);
    const inputContext = useContext(InputParamsContext);

    const fetchGeneral = (jsonparams) => {
        if (postals.length >= 2) {
            fetch("https://data.rcc-acis.org/General/"+stateType, {body: jsonparams, method: "POST", headers: {'Content-Type': 'application/json'}})
                .then(response => {
                    if (!response.ok) {
                        setFetchError("Error fetching the list");
                        return;
                    }
                    return response.json()
                })
                .then(response => {
                    if (response.meta && response.meta.length) {
                        setCounties(response.meta);
                        setSelectedIndex(0);
                        inputContext.updateInputParams({bbox: response.meta[0].bbox});
                    } else {
                        setFetchError("No list for "+postals);
                    }
                })
                .catch(err => {
                    if (err.message.includes("Failed to execute 'createObjectURL' on 'URL'")) {
                        setFetchError("Invalid request");
                    } else {
                        setFetchError(err.message);
                    };
                })
                .finally(() => {
                    //setLoading(false);
                });
        } else {
            setFetchError("Specify valid state above");
            setLoading(false);
        }
    };

	const handleClickListItem = (event) => {
		setAnchorEl(event.currentTarget);
	};

	const handleMenuItemClick = (event, index) => {
		setSelectedIndex(index);
		setAnchorEl(null);
		inputContext.updateInputParams({bbox: counties[index].bbox});
	};

	const handleClose = () => {
		setAnchorEl(null);
	};

    useEffect(() => {
		// Reset 
		setFetchError();
		setLoading(true);
        setCounties(null);
        // Get list
        const jsonparams = JSON.stringify({state: postals, meta: "name,id,bbox"});
        fetchGeneral(jsonparams);
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [ postals, stateType ]);

	return (
        <>
        {counties &&
            <>
            <List aria-label={stateType+" selection"}>
                <ListItem
                    button
                    aria-haspopup="true"
                    aria-controls={stateType+"-menu"}
                    aria-label={"selected "+stateType}
                    onClick={handleClickListItem}
                >
                    <ListItemText 
                        primary={"Click to select "+stateType} 
                        secondary={counties[selectedIndex] ? counties[selectedIndex].name.replace(/County|Parish/gi,"").substr(0, 25) : ""} 
                    />
                </ListItem>
            </List>
            <Menu
                id={stateType+"-menu"}
                anchorEl={anchorEl}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose} 
            >
                {counties.map((option, index) => (
                    <MenuItem
                        key={option.id}
                        selected={index === selectedIndex}
                        onClick={(event) => handleMenuItemClick(event, index)}

                    >
                        {option.name.replace(/County|Parish/gi,"")}
                    </MenuItem>
                ))}
            </Menu>
            </>
        }
        {loading &&
            <>
            <Typography>
            <CircularProgress size="1em" />
            </Typography>
            </>
        }
        {fetchError &&
            <p>{fetchError}</p>
        }
        </>
	);
 };

export default SubstateSelect;
