import React, { useEffect, useState, useContext } from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import CircularProgress from "@material-ui/core/CircularProgress";
import InputParamsContext from './InputParamsContext';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
	progressContainer: {
        margin: "1.5em 3em",
        textAlign: "center",
    },
    progressText: {
        margin: 0,
        padding: "6px",
        color:  "rgba(0,0,0,0.54)",
    },
}));

const SubstateSelect = (props) => {
	const { postals, stateType } = props;
    const [anchorEl, setAnchorEl] = useState(null);
    const [selectedIndex, setSelectedIndex] = useState(0);
    const [counties, setCounties] = useState();
    const [fetchError, setFetchError] = useState();
	const [loading, setLoading] = useState(false);
    const inputContext = useContext(InputParamsContext);
    const classes = useStyles();

    const fetchGeneral = (jsonparams) => {
        if (postals.length >= 2) {
            fetch("https://data.rcc-acis.org/General/"+stateType, {body: jsonparams, method: "POST", headers: {'Content-Type': 'application/json'}})
                .then(response => {
                    if (!response.ok) {
                        setFetchError("Error fetching the list");
                        return;
                    }
                    return response.json()
                })
                .then(response => {
                    if (response.meta && response.meta.length) {
                        setCounties(response.meta);
                        setSelectedIndex(0);
                        inputContext.updateInputParams({areaDef: stateType === 'county' ? {county: response.meta[0].id} : {bbox: response.meta[0].bbox}});
                    } else {
                        setFetchError("No list for "+postals);
                    }
                })
                .catch(err => {
                    if (err.message.includes("Failed to execute 'createObjectURL' on 'URL'")) {
                        setFetchError("Invalid request");
                    } else {
                        setFetchError(err.message);
                    };
                })
                .finally(() => {
                    setLoading(false);
                });
        } else {
            setFetchError("Specify valid state above");
            setLoading(false);
        }
    };

	const handleClickListItem = (event) => {
		setAnchorEl(event.currentTarget);
	};

	const handleMenuItemClick = (event, index) => {
		setSelectedIndex(index);
		setAnchorEl(null);
		inputContext.updateInputParams({areaDef: stateType === 'county' ? {county: counties[index].id} : {bbox: counties[index].bbox}});
	};

	const handleClose = () => {
		setAnchorEl(null);
	};

    useEffect(() => {
		// Reset 
		setFetchError();
		setLoading(true);
        setCounties(null);
        // Get list
        const jsonparams = JSON.stringify({state: postals, meta: "name,id,bbox"});
        fetchGeneral(jsonparams);
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [ postals, stateType ]);

	return (
        <>
        {counties &&
            <>
            <List aria-label={stateType+" selection"}>
                <ListItem
                    button
                    aria-haspopup="true"
                    aria-controls={stateType+"-menu"}
                    aria-label={"selected "+stateType}
                    onClick={handleClickListItem}
                >
                    <ListItemText 
                        primary={"Click to select "+stateType} 
                        secondary={counties[selectedIndex] ? counties[selectedIndex].name.replace(/County|Parish/gi,"").substr(0, 24) : ""} 
                    />
                </ListItem>
            </List>
            <Menu
                id={stateType+"-menu"}
                anchorEl={anchorEl}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose} 
            >
                {counties.map((option, index) => (
                    <MenuItem
                        key={option.id}
                        selected={index === selectedIndex}
                        onClick={(event) => handleMenuItemClick(event, index)}

                    >
                        {option.name.replace(/County|Parish/gi,"")}
                    </MenuItem>
                ))}
            </Menu>
            </>
        }
        {loading &&
            <div className={classes.progressContainer}>
                <CircularProgress size="1em" />
                <p className={classes.progressText}>Loading</p>
            </div>
        }
        {fetchError &&
            <p>{fetchError}</p>
        }
        </>
	);
 };

export default SubstateSelect;
