import React, { useEffect, useRef, useState } from 'react';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import DisplayRequest from './DisplayRequest';
import MapboxMap from './MapboxMap';

const useStyles = makeStyles((theme) => ({
	downloadButton: {
		margin: "1em 0.5em",
	},
	mapImage: {
		width: "100%",
	},
}));

const DisplayMap = (props) => {
	const { imgsrc, submittedParams } = props;
	const [showParams, setShowParams] = useState(false);
	const classes = useStyles();
	const imgRef = useRef();

	const handleShowParamsClick = () => {
		setShowParams(prev => !prev);
	}

//	useEffect(() => {
//		imgRef.current.focus();
//	});

	return (
		<div id="mapContainer">
			<MapboxMap 
				imgsrc={imgsrc}
			/>
			<Button variant="contained" size="small" className={classes.downloadButton}>Download image (png)</Button>
			<Button variant="contained" size="small" className={classes.downloadButton}>Download image (geotiff)</Button>
			<Button variant="contained" size="small" className={classes.downloadButton}>Download data (json)</Button>
			<Button variant="contained" size="small" className={classes.downloadButton} onClick={handleShowParamsClick}>Display request (json)</Button>
			<br />
			{showParams &&
				<DisplayRequest 
					submittedParams={submittedParams}
				/>
			}
		</div>
	);
}

export default DisplayMap;

