import React, {useState} from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import Banner from './Banner';
import OptionsSelection from './OptionsSelection';
import GetResults from './GetResults';
import InputParamsContext from './InputParamsContext';
import { default_params, drawerWidth } from '../utilities/constants';

const useStyles = makeStyles((theme) => ({
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(2),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: 0,
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: drawerWidth,
  },
}));

const Main = () => {
  const classes = useStyles();
  const [open, setOpen] = useState(true);
  const [viewMap, setViewMap] = useState(false);
  const [inputParams, setInputParams] = useState(default_params)

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const handleViewMapClick = () => {
    setViewMap(true);
  };

  const updateInputParams = (updateParams) => {
    setViewMap(false);
    const newParams = {...inputParams, ...updateParams}
    setInputParams(newParams);
  }

  return (
    <InputParamsContext.Provider value={{ inputParams, updateInputParams }}>
      <>
        <Banner
          open={open}
          handleDrawerOpen={handleDrawerOpen}
        />

        <nav>
          <OptionsSelection
            open={open}
            handleDrawerClose={handleDrawerClose}
            handleViewMapClick={handleViewMapClick}
          />
        </nav>

        <main>
          <div className={classes.drawerHeader}  />
          <div className={clsx(classes.content, { [classes.contentShift]: open, })}>
            {viewMap &&
              <GetResults />
            }
            {!viewMap &&
              <>
                <p>... Introductory text here ... Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent in orci scelerisque erat tincidunt dictum. Sed quis tortor quis tortor luctus euismod. In vitae libero est. Nulla a metus vitae nibh fringilla volutpat. Sed luctus aliquet euismod. Ut sodales justo eget efficitur sollicitudin. Duis mattis nec massa non ullamcorper. Etiam convallis a lorem ac sodales. Curabitur erat risus, tempor ut nisl eu, dignissim tincidunt elit. Fusce id vehicula magna. Fusce blandit eu magna fringilla elementum. Pellentesque mollis, eros ac iaculis euismod, dui leo fermentum tortor, in tempus erat elit a neque. Phasellus aliquet felis eu erat dictum fermentum. Proin consequat fringilla mattis. Ut eget leo ac ipsum feugiat condimentum.
                   ... Set options and click "Submit Request" button to view results.</p>
              </>
            }
          </div>
        </main>
      </>
    </InputParamsContext.Provider>
  )
}

export default Main;
