import React, {useState} from "react";
import CircularProgress from '@material-ui/core/CircularProgress';

const ViewGeotiff = () => {

	const [mapBlob, setMapBlob] = useState();
	const [mapError, setMapError] = useState();
	const [loading, setLoading] = useState(false);

	const fetchMap = () => {
		const input_params = JSON.stringify({
			state:"ne,ok",
			grid:"1",
			date:"2012-6",
			elems:[{
				name:"maxxt",
				interval:"mly",
				duration:"mly",
				reduce:"max",
			}],
			output:"image",
			image:{
				proj:"lcc",
				overlays:["county:1:gray","state:2:purple"],
				interp:"cspline",
				width:350,
				levels:[90,95,100,105,110],
			},
		});

		// Reset 
		setMapBlob();
		setMapError();
		setLoading(true);

		fetch("http://data.rcc-acis.org/GridData?params="+input_params)
			.then(response => {
				if (!response.ok) {
					setMapError("Error fetching the map");
					return;
				}
				return response.blob()
			})
			.then(responseBlob => {
					setMapBlob(URL.createObjectURL(responseBlob));
				})
			.catch(err => {
				setMapError(err.message);
			})
			.finally(() => {
				setLoading(false);
			});
	}

	return (
		<>
			<button onClick={fetchMap}>Get Map</button>
			<div>
				{loading &&
					<CircularProgress />
				}
				{mapBlob &&
						<img src={mapBlob} alt="requested map" />
				}
				{mapError &&
					<p>Error: {mapError}</p>
				}
			</div>
		</>
	);
}

export default ViewGeotiff;
