import React, { useState, useContext } from 'react';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormLabel from '@material-ui/core/FormLabel';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';

import Grid from '@material-ui/core/Grid';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';


const useStyles = makeStyles((theme) => ({
	mainGridContainer: {
		marginTop: "1em",
	},
	vertRadioButton: {
		color: "midnightblue !important",
		padding: "6px",
		fontSize: "90%",
	},
}));

const SubstateSelect = (props) => {
	const { postals } = props;
	const inputContext = useContext(InputParamsContext);
	const [stateType, setStateType] = useState('all');
	const classes = useStyles();

	const handleTypeChange = (event) => {
		setStateType(event.target.value);
	};

	return (
		<>
			<Grid container spacing={2} className={classes.mainGridContainer}>
				<Grid item>
					<FormControl component="fieldset">
						<FormLabel component="legend">State options:</FormLabel>
						<RadioGroup column aria-label="state type" name="statetype" value={stateType} onChange={handleTypeChange}>
							<FormControlLabel 
								value="all" 
								control={<Radio size="small" className={classes.vertRadioButton} />} 
								label="Entire area" 
							/>
							<FormControlLabel 
								value="county" 
								control={<Radio size="small" className={classes.vertRadioButton}/>} 
								label="County" 
							/>
							<FormControlLabel 
								value="climdiv" 
								control={<Radio disabled size="small" className={classes.vertRadioButton} />} 
								label="Climate division" 
							/>
						</RadioGroup>
					</FormControl>
				</Grid>

				<Grid item>
					{stateType === 'county' &&
						<div>
							<List aria-label="County selection">
								<ListItem
									button
									aria-haspopup="true"
									aria-controls="county-menu"
									aria-label="selected county"
								>
									<ListItemText primary="Click to select county" secondary={"Currently selected: "} />
								</ListItem>
							</List>
						</div>
					}
				</Grid>
			</Grid>

		</>
	);
 };

export default SubstateSelect;

/*
Request URL: http://data.rcc-acis.org/General/county
{state: "ny", meta: "name,bbox"}

*/
