import React, { useState, useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

const useStyles = makeStyles((theme) => ({

}));

const CountySelect = (props) => {
	const { postals } = props;
	const inputContext = useContext(InputParamsContext);
    const [anchorEl, setAnchorEl] = React.useState(null);
	const [selectedIndex, setSelectedIndex] = React.useState(0);
    const classes = useStyles();

    const counties = [{menuName:"Broome"}, {menuName:"St. Lawrence"}]

	const handleClickListItem = (event) => {
		setAnchorEl(event.currentTarget);
	};

	const handleMenuItemClick = (event, index) => {
		setSelectedIndex(index);
		setAnchorEl(null);
		inputContext.updateInputParams({county: counties[index]});
	};

	const handleClose = () => {
		setAnchorEl(null);
	};

	return (
        <>
            <List aria-label="County selection">
                <ListItem
                    button
                    aria-haspopup="true"
                    aria-controls="county-menu"
                    aria-label="selected county"
                    onClick={handleClickListItem}
                >
                    <ListItemText primary="Click to select county" secondary={counties[selectedIndex].menuName} />
                </ListItem>
            </List>
            <Menu
                id="county-menu"
                anchorEl={anchorEl}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose} 
            >
                {counties.map((option, index) => (
                    <MenuItem
                        key={option.menuName}
                        selected={index === selectedIndex}
                        onClick={(event) => handleMenuItemClick(event, index)}
                    >
                        {option.menuName}
                    </MenuItem>
                ))}
            </Menu>
        </>
	);
 };

export default CountySelect;

