import React, { useEffect, useRef, useState } from "react";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableRow from '@material-ui/core/TableRow';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
	table: {
		width:"9.2em",
	},
	range: {
		width:"8em",
	},
}));

const MapLegend = (props) => {
	const { imgInfo } = props;
	const { levels, cmap } = imgInfo;
	const [ width, setWidth ] = useState();
	const classes = useStyles();
	const ref = useRef(null);

	let color_styles = [];
	let color_styles2 = [];
	cmap.forEach((color) => {
		color_styles.push( {width: "1.2em", backgroundColor: color, opacity: "0.8"} );
		color_styles2.push( {width: "56px", height: "28px", backgroundColor: color, opacity: "0.8"} );
	})

	useEffect(() => {
		console.log(ref.current.offsetWidth);
		const defaultWidth = cmap.length * 56;
		console.log(defaultWidth)
		setWidth(defaultWidth)
	})

	return (
		<>
		<TableContainer>
			<Table size="small" padding="none" className={classes.table} aria-label="color table">
				<TableBody>
					<TableRow key="first">
						<TableCell style={color_styles[0]}></TableCell>
						<TableCell align="center" className={classes.range}>&lt;{levels[0]}</TableCell> 
					</TableRow>
					{levels.map((lvl, i) => (
						<TableRow key={i}>
							<TableCell style={color_styles[i+1]}></TableCell>
							{levels[i+1] ?
								<TableCell align="center" className={classes.range}>{levels[i]} to {levels[i+1]}</TableCell>
							:
								<TableCell align="center" className={classes.range}>&ge;{levels[i]}</TableCell>
							}
						</TableRow>
					))}
				</TableBody>
			</Table>
		</TableContainer>

		<TableContainer ref={ref}>
		<Table size="small" padding="none" style={{width:width}} aria-label="color table">
			<TableBody>
				<TableRow>
					{color_styles2.map((cs, i) => (
						<TableCell style={cs}></TableCell>
					))}
				</TableRow>	
			</TableBody>
		</Table>
		</TableContainer>
		</>



	);
}

export default MapLegend;
