import React, {useContext} from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';

const options = [
	{'name': '-Climate Regions-', 'states': -1},
	{'name': 'CONUS', 'value': 'us', 'states': 'WA-MN-FL-ME'},
	{'name': 'Northeast', 'value': 'ne', 'states': 'ME-PA-MD'},
	{'name': 'Southeast', 'value': 'se', 'states': 'VA-FL-AL-NC'},
	{'name': 'Upper Midwest', 'value': 'um', 'states': 'MN-IA-MI'},
	{'name': 'Ohio Valley', 'value': 'ov', 'states': 'IL-MO-TN-WV'},
	{'name': 'South', 'value': 'so', 'states': 'KS-TX-MS'},
	{'name': 'Northern Rockies & Plains', 'value': 'nr', 'states': 'MT-NE'},
	{'name': 'Northwest', 'value': 'nw', 'states': 'WA-ID'},
	{'name': 'West', 'value': 'we', 'states': 'CA-NV'},
	{'name': 'Southwest', 'value': 'sw', 'states': 'UT-AZ-CO'},
	{'name': '-Regional Climate Centers-', 'states': -2},
	{'name': 'Northeast', 'value': 'nrcc', 'states': 'ME-WV'},
	{'name': 'Southeast', 'value': 'srcc', 'states': 'VA-FL-AL-NC'},
	{'name': 'Midwestern', 'value': 'mrcc', 'states': 'MN-IA-MO-OH'},
	{'name': 'Southern', 'value': 'sercc', 'states': 'TX-OK-TN'},
	{'name': 'High Plains', 'value': 'hprcc', 'states': 'ND-WY-KS'},
	{'name': 'Western', 'value': 'wrcc', 'states': 'CA-MT-NM'},
	{'name': '-NWS Regions-', 'states': -3},
	{'name': 'Eastern', 'value': 'nwser', 'states': 'ME-OH-SC'},
	{'name': 'Southern', 'value': 'nwssr', 'states': 'NM-OK-TX-FL'},
	{'name': 'Central', 'value': 'nwscr', 'states': 'WY-MN-MI-KY-MO'},
	{'name': 'Western', 'value': 'nwswr', 'states': 'WA-MT-CA'},
	{'name': 'Alaska', 'value': 'nwsar', 'states': 'AK'},
	{'name': 'Pacific', 'value': 'nwspr', 'states': 'HI'},
];

const useStyles = makeStyles((theme) => ({
	listContainer: {
		border: "1pt solid lightgray",
	},
}));

const RegionSelect = () => {
	const [anchorEl, setAnchorEl] = React.useState(null);
	const [selectedIndex, setSelectedIndex] = React.useState(1);
	const inputContext = useContext(InputParamsContext);
	const classes = useStyles();

	const handleClickListItem = (event) => {
		setAnchorEl(event.currentTarget);
	};

	const handleMenuItemClick = (event, index) => {
		setSelectedIndex(index);
		setAnchorEl(null);
		inputContext.updateInputParams({state: options[index].states.replaceAll('-',',')});
	};

	const handleClose = () => {
		setAnchorEl(null);
	};

	return (
		<div>
			<List className={classes.listContainer} aria-label="Region selection">
				<ListItem
					button
					aria-haspopup="true"
					aria-controls="reguib-menu"
					aria-label="selected region"
					onClick={handleClickListItem}
				>
					<ListItemText primary="Click to select region" secondary={"Currently selected: "+options[selectedIndex].name} />
				</ListItem>
			</List>
			<Menu
				id="element-menu"
				anchorEl={anchorEl}
				keepMounted
				open={Boolean(anchorEl)}
				onClose={handleClose} 
			>
				{options.map((option, index) => (
					<MenuItem
						key={index}
						selected={index === selectedIndex}
						disabled={option.states < 0}
						onClick={(event) => handleMenuItemClick(event, index)}
					>
						{option.name}
					</MenuItem>
				))}
			</Menu>
		</div>
	);
}

export default RegionSelect;
